--
-- 表的结构 `school_essay`
--

CREATE TABLE IF NOT EXISTS `school_essay` (
  `id` int(100) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `mess_user` varchar(32) NOT NULL COMMENT '发布者用户名',
  `img` varchar(32) NOT NULL COMMENT '主题图片',
  `message_tittle` varchar(32) NOT NULL COMMENT '信息标题',
  `message_more` text NOT NULL COMMENT '信息内容',
  `time` date NOT NULL COMMENT '信息发布时间',
  `look` int(100) NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `collect` int(100) NOT NULL DEFAULT '0' COMMENT '收藏次数',
  `good` int(100) NOT NULL DEFAULT '0' COMMENT '赞次数',
  `bad` int(100) NOT NULL DEFAULT '0' COMMENT '举报次数',
  `strong` int(100) NOT NULL DEFAULT '0' COMMENT '推荐指数 ',
  `only_me` tinyint(1) NOT NULL DEFAULT '0' COMMENT '仅自己可见',
  `evl` int(32) NOT NULL DEFAULT '0' COMMENT '评论次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='校园文章信息信息发布' AUTO_INCREMENT=6 ;

--
-- 转存表中的数据 `school_essay`
--

INSERT INTO `school_essay` (`id`, `mess_user`, `img`, `message_tittle`, `message_more`, `time`, `look`, `collect`, `good`, `bad`, `strong`, `only_me`, `evl`) VALUES
(1, '1576701411@qq.com', '55ea9f365a9b2.jpg', '你说自己很孤独，你配吗？', '<p style="line-height: 3em;">我们无时无刻不讨厌孤独，又无时无刻不在标榜着自己的孤独。<br/></p><p style="line-height: 3em;">它就像衣服上那与生俱来的标签，和每一寸肌肤形影不离。我们有时想将其撕去来获得更好的舒适度，有时又想让其锋芒毕露来显示自己的与众不同。</p><p style="line-height: 3em;">我有个室友，姑且称之为老顾，今年二十又七，没个正经工作，终日宅在家中操持股票生意。他倒也没啥不良嗜好，无聊时就玩玩lol看看二次元，吃穿用度也均花费不大，总也能得过且过下去。</p><p style="line-height: 3em;">有一次我房卡忘带没法进门，拿了老顾的手机打了许久电话才将其解决。为了报答这一恩情，我拿到工资后就提议请他喝酒吃肉，老顾也没扭捏，带我去一家价廉物美的自助烤肉店大快朵颐。</p><p style="line-height: 3em;">酒过三巡，老顾的话也自然多了起来。</p><p style="line-height: 3em;">“听说你在做文案策划，身边的妹子肯定很多吧？”</p><p style="line-height: 3em;">“哪有哪有，姑娘再多再漂亮，也跟自己没半点关系啊。”我一笑了之。</p><p style="line-height: 3em;">“那你之前谈过恋爱？”</p><p style="line-height: 3em;">“谈了半年，分了。”我倒也实话实说。</p><p style="line-height: 3em;">“你好歹还谈过，我现在连姑娘的手还没牵过呢。”老顾挠了挠即将谢顶的脑袋，满脸无奈。</p><p style="line-height: 3em;">我乍一听还大吃一惊，看看老顾落寞的神情，觉得这也在情理之中。一个即将秃顶的矮胖青年，没有高薪稳定的工作，没有口吐莲花的口才，甚至连兴趣爱好都那么泯然众人，至今单身也并非是天方夜谭。</p><p style="line-height: 3em;">“我吧，觉得自己真他妈孤独。做着自由职业，同事自然是没有。我呢也不喜欢社交，没事儿就宅在家里。喜欢的姑娘也远在天边，恐怕一辈子都见不着面。活得真是憋屈。”老顾话也渐渐多了起来。</p><p style="line-height: 3em;">“就没想过要改变现状？”我想为老顾出谋划策。</p><p style="line-height: 3em;">“有啊，我下了这个，你看，你们年轻人爱玩的，周围都是年轻貌美的姑娘,也不知道是真的假的。”老顾像发现新大陆一般，兴致勃勃地摆弄着那个app。</p><p style="line-height: 3em;">我对这个app兴味索然，但却对它的广告语记忆犹新：陌生并不存在，因为我们有同样的孤独。</p><p style="line-height: 3em;">老顾自称是孤独的，他的个性签名源于里尔克的《秋日》：谁此刻孤独，谁就此生孤独。</p><p style="line-height: 3em;">而在他眼中，孤独不是继续坚持自我，而是通往新世界的一扇窗户。每个孤独的人，都渴望找到同样孤独的同类。孤独，是他们结伴成群的接头暗号。</p><p style="line-height: 3em;">他们一边极力逃避孤独，一边用孤独来标榜自己的遗世独立,并以此为通道企图摆脱孤独，这真是莫大的讽刺。</p><p style="line-height: 3em;">待到酒足饭饱，杯盘狼藉，老顾略带羞涩地翻开他写过的文章，并试图让我评价一番。老顾的文章只有一个主题，那就是“孤独”，这个名词翻来覆去变换着各种花样，却给他的文章平添了诸多荷尔蒙的气息。文笔也没有饱经风霜的凝练感，各式华丽的修辞也掩埋不了主题的乏味，就像四月份腐烂的樱桃，甜腻得叫人望而却步。若将此文笔同他邋遢谢顶的长相联系起来，那绝对是每个异性的噩梦。</p><p style="line-height: 3em;">世上很多人，尤其是所谓文艺青年们，善于将孤独一词挂在嘴边，他们说的写的都只是翻版的小资简介，“咀嚼着青烟般的烦恼”，却无力超越亦无力洞察时代和自己。他们不够狠也不够极致，喜欢沉溺自我，又希冀得到大众的欣赏，没有真正的特立独行，又缺少聆听大众声音的能力和诚意。</p><p style="line-height: 3em;">他们常常以孤独自诩，认为“举世皆浊我独清，众人皆醉我独醒”，但看破红尘的表象背后，却洋溢着对社交和约炮的原始渴望。孤独是他们解释和拒绝一切的武器，不善交际可以用孤独修辞美化，性格木讷称作是游离于俗世外的灵魂，而好吃懒做也被贴上“不愿与世人同流合污”的标签。孤独也是他们寻找认同感的标识，“这世界孤独地需要一个同类”此等语句层出不穷，彰显了他们对异性和交际的不懈追求，而孤独则为空虚的灵魂穿上一件皇帝的新装，只有同样空虚的灵魂才对其臭味相投。</p><p style="line-height: 3em;">李志在Twitter中说道：真应该把“我们生来就是孤独”改成“我生来就是孤独”。的确，孤独一词如今已沦为廉价的配饰，泛滥在各种无病呻吟的堕落青年中，成为他们不合群和不求上进的说辞，甚至被当做约炮的口号大肆叫卖。</p><p style="line-height: 3em;">真正的孤独是什么，我不得而知。当梵高留下超越时代的作品，仰面朝天对着太阳，绝望地按下左轮手枪时，他的眼中只有星空，没有人世；当尼采不肯与世界达成共识，却对着被人驱车鞭打的老马痛苦流涕：我受苦受难的兄弟，他已笃定再也找不到同类。他们的孤独，我难以理解，却心生敬佩。</p><p style="line-height: 3em;">也许孤独注定是属于天才和疯子的，而庸俗如我们，所拥有的只有空虚和寂寞。不要总说自己很孤独，因为你真的不配。</p><p><br/></p>', '2015-09-05', 679, 0, 1, 0, 0, 0, 1),
(2, '1576701411@qq.com', '55eabdc6763ab.jpeg', '我所见过的最有范儿的女人', '<p style="line-height: 3em;"><strong>1</strong><br/></p><p style="line-height: 3em;">上次回家见到刚刚高考结束的侄女，我问她准备报考哪里，她说咨询过小姑的意见了，只要是北上广一线城市的学校，学费她包了。</p><p style="line-height: 3em;">我只能啧啧称奇。小姑是我们家族的传奇人物，几乎颠覆了家里的规矩，也是我目前见过的在农村里长大的最有范儿的女人，无论是性格还是修为。</p><p style="line-height: 3em;"><strong>2</strong></p><p style="line-height: 3em;">小姑其实不是我的小姑，是我的一位远房亲戚，按照辈分来说，我也能管叫小姑。</p><p style="line-height: 3em;">我大学快毕业的时候，小姑已经三十多了，还是个姑娘。那个年纪在农村还没嫁人就像是怪物一样要被人指指点点的，小姑的父母自然遭受四面八方围攻过来的闲言闲语。</p><p style="line-height: 3em;">小姑只读到初中就没读下去了，而跟她一起的那些同学都已经成家有了孩子，只有小姑依然一个人。</p><p style="line-height: 3em;">有一年春节我们循例去拜访，我们在客厅有说有笑，小姑跟下人一样的忙来忙去都没时间跟我们说话。中午吃饭的时候一桌子的菜都是小姑一个人张罗的。小姑比较腼腆，做桌子一边看我们说话，偶尔附和着一起笑笑。有时候我爸问起话来她就礼貌的回应几句，我甚至觉得她跟这个家有些格格不入了，俨然成了一个外人。</p><p style="line-height: 3em;">下午我在房间里看电视，小姑在拖地，我在院子里看他们打牌，小姑在厨房里收拾。回家的时候我就问我爸，小姑到底是不是他们家亲生的啊？</p><p style="line-height: 3em;">爸妈跟我说了很多小姑的事儿，也是后来才知道小姑是他们家最后生的一个女儿，从小脾气倔强，那时候农村人认为生个女儿像是赔本的买卖，并没有女儿富养的概念。小姑是吃粗粮长大的，家里的好吃好穿都让给三个哥哥了。小姑上学的时候用的书包和文具什么的都是哥哥们不要的或者剩下来的，父母觉得女子无才便是德，上学还不如在家干活。小姑那时候白天干活，夜里点蜡烛看书，也是成绩好，老师三番五次家访要求家里重视小姑的学习成绩，后来父母也就半推半就的让小姑读书了，一直到了初中。</p><p style="line-height: 3em;">小姑的三个哥哥读书走马观花做任务，没上初中就被革命队伍淘汰了，只有小姑争气读上了初中，并且成绩非常好。在小姑的计划里她还要上高中考大学，不过这念头很快被父母打消了。</p><p style="line-height: 3em;">初中毕业之后，小姑进了一家服装厂工作，那时候流行男孩子学木工，女孩子进厂。到了婚配年龄，基本上能算的上是革命友谊了，女工配木匠是那个年代的最佳婚姻组合。</p><p style="line-height: 3em;"><strong>3</strong></p><p style="line-height: 3em;">小姑虽然对家里妥协了，但是并没有对命运妥协。</p><p style="line-height: 3em;">小姑将自己在读书上的韧劲儿放在工作上了，进厂不到一年已经是车间里手艺活儿最好的员工，那时候机器很少，很多质检工作都要靠人工手动检查。小姑的针线活很好，很短时间内分辨了布料和成衣的区别，并且在手工缝制上有了很多想法和建议，厂里的一些老师傅不禁小姑刮目相看。</p><p style="line-height: 3em;">我印象里几次去小姑的家里，看到中堂上的刺绣，阳台的窗帘以及房间里面的布艺装饰品都是小姑的手艺，小姑放下了书本却捡起了很多人已经放弃的手艺活儿，而且出类拔萃。</p><p style="line-height: 3em;">小姑的第一个对象是爸爸的战友介绍的，也是个军人，叫中军。据说中军的父亲再一次执行任务中救了他战友，自己翻车不治导致下半身瘫痪，后来中军应征入伍，战友视如亲子。</p><p style="line-height: 3em;">那年大雪特别大，父亲的战友算是南京军区的干部，特批他开着部队的车回家，顺便去见小姑。</p><p style="line-height: 3em;">小姑的家人忙着团团转，还特地张罗了很多菜，对他们来说来的毕竟是一位首长。小姑并没有很激动，只是在房间里看书，一会儿看着窗外的大雪，趴在桌子上，只是觉得爱情离自己还很遥远，婚姻就更不会现在去考虑了。</p><p style="line-height: 3em;">中军的吉普车被雪裹成了白色，一下车就标准的军礼向二老致敬。小姑在客厅并不拘谨，显得见过了大场面的人，中军走过去向小姑敬了个标准的军礼，并说，婉莹姑娘你好！小姑淡淡的笑了笑，回了句，你好。</p><p style="line-height: 3em;">中军并非出身世家，当兵之后性格方面都很直截了当，也不太会拐着弯。而小姑虽然农村长大却满身的文化味，举手投足俨然是大家闺秀，这也是后来中军对她的评价。</p><p style="line-height: 3em;">我很好奇他们相亲的时候，小姑会跟中军交谈着什么。一个内心不安分、求知欲望强烈的准知识分子，一个是很早缀学入伍当兵的粗人，他们的话题会是什么。</p><p style="line-height: 3em;">中军临走的时候小姑送了一本《林海雪原》给他。中军其实并不怎么读书，但是这本书他看了好几遍，因为书中说的地方正是他后来被派往的东北。</p><p style="line-height: 3em;">中军跟小姑通过几次书信，中军信里大致说了东北的生活环境，冰天雪地和守卫边疆的荣誉感，小姑并没有跟他说家里长短，她说的都是一些平时没办法跟同事和家人交流的话，还有书本和小说上的一些事儿，那段时间可能觉得中军唯一能明白她内心的人。</p><p style="line-height: 3em;">中军后来一次跟父亲通电话说到小姑的时候支支吾吾的也说不上来，后来我觉得他可能并不是小姑想的那样，中军就是一个老实守纪律的军人而已，他的内心世界并不是那么丰富。而小姑的意思是，只要他勇敢一点，当时差点放弃了自己引以为傲的世界观跟他走了。</p><p style="line-height: 3em;"><strong>4</strong></p><p style="line-height: 3em;">中军后来在东北办了婚礼，妻子是当地人，小姑还写信道贺。</p><p style="line-height: 3em;">小姑的家人一直觉得小姑太不懂事拒绝了中军，小姑在家里的地位更加艰难。又过了几年小姑在已经是厂子的骨干员工，除了质检工作之外，送过来的样衣设计她也要参与督导工作，她后来也迷上了服装设计，这种太好学的观念跟家人的安分守己的三观又有了强烈冲突。</p><p style="line-height: 3em;">小姑二十五岁的时候，上门做媒的人几乎踏破了门槛，但是小姑没一个能看的上的，时间一长村儿里就会有闲言碎语，谁谁家里养了个老姑娘啥的这种没把门的风言风语自然就传出去了。小姑的父母很好面子，在家也没给好脸色，有时候甚至直接骂：我们家都养你这么多年了，你还好意思待着啊！</p><p style="line-height: 3em;">虽然是亲生女儿，但是大了嫁不出去就觉得非常嫌弃，尽管她那时候才二十五岁。</p><p style="line-height: 3em;">小姑在家里什么都做，什么都可以跟父母妥协，唯独自己的爱情和婚姻她是一定要自己做主的。我一直觉得小姑是个非常了不起的女人，那个年代别说你给自己婚姻做主了，你在家大声说话的资格都没有啊。即便如此，小姑还是撑过来了。</p><p style="line-height: 3em;">冲突的比较厉害的一次是因为家里人骗小姑回来说父亲生病，其实是安排好了相亲。对方是隔壁县城的土豪，儿子三十多了一直没娶上媳妇儿是因为小时候从马车上摔过，脑子有点不太好。小姑明白是相亲之后虽然很生气不过还是顺着父母的意思，跟之前一样糊弄过去。只是没想到父母跟人家谈的不是相亲，而是结婚的彩礼，才知道这事儿的性质不是相亲，简直是在做买卖。再看对方那傻儿子，见到小姑清秀的模样居然当众流起了口水，还拉着他爸嚷着要娶小姑。</p><p style="line-height: 3em;">小姑没说话，只是看着自己父母的态度，而父母却在跟人家商量彩礼的事儿。</p><p style="line-height: 3em;">小姑一气之下收拾了自己的行李搬到厂子里住，好几个礼拜没回家。</p><p style="line-height: 3em;"><strong>5</strong></p><p style="line-height: 3em;">小姑在厂里开始研究服装设计，自己学着绘图，自从车间引进了一批国外样板之后小姑就每日每夜的开始钻研和画图，几个月下来画稿也比较成熟。车间里其他的女工看不出来小姑画的是啥，但是她自己做梦都会笑醒。</p><p style="line-height: 3em;">家里最小的儿子，也就是小姑的三哥结婚。小姑的三嫂家里很阔气，希望接亲的亲友都来，按道理小姑作为未出阁的姑娘不用去接亲的，但是对方家里好客也就跟着车队去了。迎亲队伍浩浩荡荡排起长龙停在门口，当地嫁女儿的礼俗很多，大哥二哥都是结过婚的人，所以只有小姑给三个递挑杆，挑开新娘子的头盖。吸引小姑关注的不是新娘子，而是新娘子的妈妈，在一边哭成累人一样舍不得，心想着，这样的有钱人家也是舍不得自己女儿的。想到自己的父母，不禁鼻子一酸，一个人躲起来哭了一场。</p><p style="line-height: 3em;">小姑后来搬回家住，夜里跟母亲聊天，说，妈，都怪你没把我生成个男孩，现在到好，女儿身，男孩个性。她自己说着这些想到当初三嫂走的时候，她妈妈哭成个泪人的样子，然后眼睛一红跟母亲说，妈，对不起，这些年我比三个哥哥还让你烦，我没好好听你话。</p><p style="line-height: 3em;">老太太心头一颤把小姑搂在怀里不说话，小姑抱着母亲，老太太的眼泪大颗的砸到她额头上。</p><p style="line-height: 3em;">小姑和家里人的关系算是有了重要缓和，父母要是在村儿里听到些闲言碎语的也能直接趾高气扬的说，我女儿就是个宝贝，宝贝就得放家里养着，谁想要哪那么容易啊！</p><p style="line-height: 3em;">小姑就这样在家里又待几年，三十了，还是个大姑娘。</p><p style="line-height: 3em;"><strong>6</strong></p><p style="line-height: 3em;">我跟小姑碰面很少，基本上没什么交集，我是大学快毕业的时候才知道小姑嫁人了。那是我跟侄女通电话的时候她说，小姑已经嫁人了。</p><p style="line-height: 3em;">我说，她终于想通了不做老姑娘啦？</p><p style="line-height: 3em;">侄女说，是啊，她又干了一件惊天动地的事儿！</p><p style="line-height: 3em;">本来是通电话说侄女学习的事儿，然后一个多小时的电话又在八卦小姑的英雄事迹了。</p><p style="line-height: 3em;">三十多岁的小姑成为了厂里最老的厂花，不少人背后议论纷纷，厂里的领导也跟着着急小姑的婚姻大事，不停的给她张罗对象相亲，不过都被小姑婉拒了。</p><p style="line-height: 3em;">小姑后来的老公是青岛的一位自己创业的服装设计师吴岩，他的老同学是小姑的老乡。吴岩在青岛和朋友合伙做了一个独立工作室，主要是做服装设计，因为考虑成本的问题，他想通过一些小地方的厂子做后面的供货。在老同学家乡度假的时候偶尔逛到了小姑的厂子就找厂子参观了下，在车间看到了小姑的服装手稿，一下子惊讶的说不出话来。</p><p style="line-height: 3em;">小姑拧不过侄女的死缠烂打就说了当时见到吴岩的时候的情景。吴岩拿着手稿急匆匆的找到小姑，小姑那时候正在办公室裁样衣，一抬头看到气喘吁吁的吴岩。吴岩后来说被小姑的一双大眼睛吓到，简直跟浸过水一样清澈。</p><p style="line-height: 3em;">小姑也没见到过这样的男人，但是没说话心就砰砰跳，也不知道怎么回事。</p><p style="line-height: 3em;">吴岩开口激动的有点结巴，这个…这个都是你画的吗？</p><p style="line-height: 3em;">小姑点点头。</p><p style="line-height: 3em;">据说后来吴岩跟小姑在厂房里谈了很久，从传统的针线活儿到现代服装设计，吴岩还给她的稿子上提了很多中肯的修改意见，小姑的设计从开始被人嘲笑到被专业领域的人认可，距离上一次这样的兴奋还是在小学的时候第一次获得奖状被老师夸奖的样子。</p><p style="line-height: 3em;">吴岩征求了小姑的同意带走了几张画稿，回去之后做了修改，三个月之后小姑就收到了吴岩的样衣，是一件非常漂亮的雪纺裙，同事们都惊呆，无论从做工和样式到用材都极具的讲究。晚上小姑带回家，挂在放家里仔细看着，吴岩的包裹里还有一封信，上面写着：这是你第一件十分优秀的作品，也是我目前见过最惊艳的作品。</p><p style="line-height: 3em;">小姑看完之后就哭了，我想她三十多年来都不曾这样的哭过。</p><p style="line-height: 3em;"><strong>7</strong></p><p style="line-height: 3em;">吴岩第二次来的时候送给了小姑另一件套装，并且告诉她这件衣服在他老家那边只有结婚才穿的。小姑先是很紧张，后来又很害怕，但是她知道她无法拒绝，紧紧的攥在手里。</p><p style="line-height: 3em;">小姑家里人反对她跟吴岩结婚，理由是青岛太远了，老太太反对到不是因为吴岩不好，是十多年来赶小姑都赶不走，这一下子她要是真走了估计受不了。她觉得以后离女儿那么远看都不能看上一眼，根本无法忍受。</p><p style="line-height: 3em;">小姑吃了三天闭门羹，老太太坚决不同意这门婚事。吴岩因此拖了几天才回的青岛处理自己公司的事儿。村儿里又传来风言风语，说是小姑被人骗的稀里糊涂的，又说二老家门不幸，生了个专挑事儿的祸端女儿。</p><p style="line-height: 3em;">小姑央求了好几天没见效果就默默的收拾行李要去青岛，老太太后来跟她说，只要你不嫁那么远，你怎么样都行，哪怕你一辈子不嫁人在家陪我，我也乐意。</p><p style="line-height: 3em;">小姑有点不忍心，想起来当初自己求她一样，没想到母女俩相隔十年之后为同一件事儿央求对方。我一直以为小姑是那种做起事儿来很歹毒的性格，其实后来也才明白，小姑很小的时候就已经明白自己是怎样的一个人，要过怎样的生活，和去爱什么样的人。</p><p style="line-height: 3em;">对她来说，如果不是她想要的，即便孤独终老也在所不惜。这种观念在那个年代说出来简直要吓死人，村儿里奉守传统的老人们光吐沫星子就能把人淹死。</p><p style="line-height: 3em;"><strong>8</strong></p><p style="line-height: 3em;">吴岩一个月之后风风光光的来娶小姑，小姑那时候特随性，她穿着吴岩送的衣服，在二楼眺望着，一直等到听到爆竹声，看到吴岩的车队从公路排到村口，足足有几十辆车，当时村里围观的人倾巢出动，大家都感叹，原来小姑是憋着劲儿嫁了个王老五啊。</p><p style="line-height: 3em;">老太太站站在门口抱着小姑，没留一滴泪。都说出嫁的女儿老母亲要哭成个泪人，但是老太太很坚强的跟小姑说，想家就回来。小姑也特别争气全程笑脸，上了吴岩的车，回头看了一眼老家，看了看跟她挥手的父母，撇过身子，眼泪瞬间跟泄洪一样。</p><p style="line-height: 3em;"><strong>9</strong></p><p style="line-height: 3em;">今年端午回家见到侄女，我们说起她考大学的事儿，说着说着又扯到了小姑。</p><p style="line-height: 3em;">小姑已经收购了她原来的厂子成为青岛公司的厂商了，又把老家门口到镇上给修了宽马路，还给村儿里盖了一座幼儿园，以后村儿里的小孩上学就不用赶十几里路到镇上了。</p><p style="line-height: 3em;">我问侄女，小姑为啥说你考上了北上广任何一所学校就全包了学费啊？</p><p style="line-height: 3em;">侄女告诉我，有一次跟小姑通电话说考大学的事儿，小姑就很严肃的告诉她，现在这个年代不像七八十年代了，死读书是没什么用的，但是好的学校能给你好的认知，也能告诉你怎么去读书才有用，不像我那时候只能靠读书长见识，现在世界变化这么大一天不学就落后。</p><p style="line-height: 3em;">侄女问小姑，都是大学，干嘛一定要去大城市啊？</p><p style="line-height: 3em;">小姑后来也没具体说，只是回青岛后发来的一封邮件里告诉了侄女说，我那时候想逃离农村不是不爱家乡，而是逃离这样的固封的观念和圈子，让你去大城市也不是一定要固定在大城市的那个圈子，是让你思想一定要走在前面，步子才能跟得上，以后在生活上才少走些弯路，在事业上少干一些后悔的事儿，在爱情上少爱一些不该爱的人。</p><p style="line-height: 3em;">侄女没能看懂这些话，我听了她的撰述有些吃惊，暗自佩服小姑。这些我们可能都知道的道理但是想不通的事儿，她在很年轻的时候已经想通了，而且一直做的很好。</p><p style="line-height: 3em;">我一直觉得小姑是农村里走出来的巾帼英雄，也是我见过最有范儿的女人。</p><p><br/></p>', '2015-09-05', 99, 0, 0, 0, 0, 0, 1),
(3, '1576701411@qq.com', '', '用正确的姿势听英文歌', '<p style="line-height: 3em;">用正确的姿势听英文歌（你假如在其他地方读过，那也是我写的。）<br/></p><p style="line-height: 3em;">基本式</p><p style="line-height: 3em;">「学长，请问怎样才能把英语练得和你一样棒？」</p><p style="line-height: 3em;">「多听英文歌呗。」</p><p style="line-height: 3em;">「噢。」于是学妹默默打开了手机中的Young For You，学长在一旁沉默不语。</p><p style="line-height: 3em;">我说矜持高冷的学长，为何您就不肯告诉学妹她姿势不对呢？</p><p style="line-height: 3em;">那么，究竟怎么样的姿势才是听英文歌学英文的正确姿势？基本要领如下：</p><p style="line-height: 3em;">1.	两情相悦：找一首你中意的外国歌手的你喜欢的歌，歌手最好英语为母语。</p><p style="line-height: 3em;">2.	初步试探：循环三遍左右，看看自己能否抓住副歌部分的主要句子，或者个别单词。</p><p style="line-height: 3em;">3.	如胶似漆：反复循环，查阅英文歌词，看看有无自己不熟悉的单词或者句型，把看起来比较装逼的那句话，背诵下来，或者记在自己的小本本上，以便日后之需。</p><p style="line-height: 3em;">4.	巫山云雨：把该歌的高潮部分设为闹钟铃声，平时也经常哼唱。</p><p style="line-height: 3em;">5.	七年之痒：对，把一首歌设为闹铃后，离你们的分道扬镳也不远了。无论你曾经多么爱这首歌。</p><p style="line-height: 3em;">6.	撕逼：对，就是这样，你再也不想听到这首歌。但是好在你对副歌的句型已经滚瓜烂熟。</p><p style="line-height: 3em;">7.	再回首：偶尔你还会想起这首歌，偶尔还会想起其中的一两句歌词，还能偶尔在自己的微博朋友圈用作暗语，表达情愫。</p><p style="line-height: 3em;">接下来让我们进入实战演练环节：</p><p style="line-height: 3em;">基本式为入门阶段，所以我为大家选取了加拿大国宝级民谣歌手Neil Young的经典之作Heart of Gold。选择乡村民谣作为基本式的好处在于，发音一般比较清晰，用词简洁明了且语速不会过快。</p><p style="line-height: 3em;">来自于加拿大的Neil Young也许是仅次于Bob Dylan的在唱作两方面均有极大的成就的民谣乐手。Heart of Gold是他1972年发表的专辑Harvest中的经典之作，也是他在Billboard上唯一的冠军单曲。</p><p style="line-height: 3em;">现在我给你5分钟的时间来听一下这首歌。然后你可以把这首歌作为你阅读余下文章的背景音乐。</p><p style="line-height: 3em;">以下是这首歌的歌词，副歌部分朗朗上课，而且用词十分简洁，但如果作为一名英语学习者，其中仍然有很多地方值得我们学习。</p><p style="line-height: 3em;">I want to live,</p><p style="line-height: 3em;">I want to give</p><p style="line-height: 3em;">I&#39;ve been a miner for a heart of gold.</p><p style="line-height: 3em;">It&#39;s these expressions I never give</p><p style="line-height: 3em;">That keep me searching for a heart of gold</p><p style="line-height: 3em;">And I&#39;m getting old.</p><p style="line-height: 3em;">Keeps me searching for a heart of gold</p><p style="line-height: 3em;">And I&#39;m getting old.</p><p style="line-height: 3em;">I&#39;ve been to Hollywood</p><p style="line-height: 3em;">I&#39;ve been to Redwood</p><p style="line-height: 3em;">I crossed the ocean for a heart of gold</p><p style="line-height: 3em;">I&#39;ve been in my mind, it&#39;s such a fine line</p><p style="line-height: 3em;">That keeps me searching for a heart of gold</p><p style="line-height: 3em;">And I&#39;m getting old.</p><p style="line-height: 3em;">Keeps me searching for a heart of gold</p><p style="line-height: 3em;">And I&#39;m getting old.</p><p style="line-height: 3em;">Keep me searching for a heart of gold</p><p style="line-height: 3em;">You keep me searching for a heart of gold</p><p style="line-height: 3em;">And I&#39;m getting old.</p><p style="line-height: 3em;">I&#39;ve been a miner for a heart of gold.</p><p style="line-height: 3em;">然后让我们来看看这首歌中有哪些值得我们学习的：</p><p style="line-height: 3em;">1. 反复出现的 heart of gold，也是第一次听歌就能抓住的部分。这里我们可以思考一个问题，为什么不用golden heart 而要使用 of 结构？golden heart是指金色的心，而heart of gold是指金子一般的心。由此我们可以得出直接用golden修饰heart，更偏向于描述其物理属性，而用of结果，更能表达抽象涵义。</p><p style="line-height: 3em;">2. I&#39;ve been to Hollywood</p><p style="line-height: 3em;">I&#39;ve been to Redwood</p><p style="line-height: 3em;">I crossed the ocean for a heart of gold</p><p style="line-height: 3em;">为什么前两句用了现在完成时，而最后一句却只是用了过去式？现在完成时的定义为：现在完成时用来表示之前已发生或完成的动作或状态，其结果的确和现在有联系。动作或状态发生在过去但它的影响现在还存在；也可表示持续到现在的动作或状态。其实前两句要表达的意思是我去过 Hollywood 和 Redwood，通常在表述去过某地时候，我们一般都会使用现在完成时have+been+to+location来进行表述。第三句仅仅是表示过去发生的事：我曾横跨大洋，所以用过去式即可。</p><p style="line-height: 3em;">3. 大家都知道Hollywood是哪儿，那Redwood呢？所以你得自己查阅一下，是指加州的红木镇。听歌也是能长知识的，当然你姿势得正确。</p><p style="line-height: 3em;">4. Keep doing sth 也是这首歌中用的比较多的结果，所以你今后遇见keep，记得后面的动词要加-ing！</p><p style="line-height: 3em;">5. get old 这个词组本身其实包含了变得越来越老的意思，所以get后直接加形容词原形即可，不需要用比较级。如果使用比较级的话，可以写成get older and older。</p><p style="line-height: 3em;">然后我们可以利用这些小小的语言点写一些自己的句子：</p><p style="line-height: 3em;">It will keep me searching the thing called love before I meet you.</p><p style="line-height: 3em;">以上仅仅是一个小小的例子，意在告诉大家：光「听英文歌学英文」一句话是远远不够的，你得用心，用正确的姿势去听，然后才能有所得。今天第一讲，入门以民谣为主，今后会逐步讲解欧美流行乐，摇滚乐以及说唱。</p><p style="line-height: 3em;">入门级其他歌曲及歌手推荐：</p><p style="line-height: 3em;">1. Blowing in the Wind-----Bob Dylan</p><p style="line-height: 3em;">2. Imagine----John Lennon</p><p style="line-height: 3em;">3. Yesterday Once More----Carpenters</p><p style="line-height: 3em;">4. Thank you for hearing me---- Sinéad O&#39;Connor</p><p style="line-height: 3em;">5. Let it be----- The Beatles</p><p style="line-height: 3em;">最后的最后，以上这些仅仅是针对那些想利用英文歌来强化自己英语能力的学习者而言的。对于音乐爱好者，听个音乐哪需要这么费劲！放松享受才是你们应该做的。</p><p style="line-height: 3em;">而且，好音乐永远在现场！</p><p style="line-height: 3em;">用正确的姿势听英文歌&nbsp; &lt;二&gt;</p><p style="line-height: 3em;">进阶篇&nbsp; 欧美流行</p><p style="line-height: 3em;">欢迎大家来到「用正确姿势听英文歌」第二讲。上一讲我们主要讲述了入门级的民谣歌曲，今天我会为大家讲解进阶篇——欧美流行。在这一讲中，我们会更加注重语态的使用，以及固定短语的搭配。希望学妹们能好好学习，追上学长的脚步。</p><p style="line-height: 3em;">第一首为大家送上Beyoncé的If I Were A Boy。这首歌可谓耳熟能详，邓紫棋在「我是歌手」上也演绎过。现在大家可以打开你的播放器，再次听一遍这首歌。</p><p style="line-height: 3em;">以下仅截取了这首歌的第一段歌词：</p><p style="line-height: 3em;">If I Were A Boy</p><p style="line-height: 3em;">If I were a boy</p><p style="line-height: 3em;">Even just for a day</p><p style="line-height: 3em;">I’d roll out of bed in the morning</p><p style="line-height: 3em;">And throw on what I wanted and go</p><p style="line-height: 3em;">Drink beer with the guys</p><p style="line-height: 3em;">And chase after girls</p><p style="line-height: 3em;">I’d kick it with who I wanted</p><p style="line-height: 3em;">And I’d never get confronted for it</p><p style="line-height: 3em;">Because they’d stick up for me</p><p style="line-height: 3em;">1. 这首歌的歌名其实就是第一大亮点。为什么I后面是用的were而不是was？仔细一想，其实很简单，因为这里使用的是虚拟语气。我个人也特别喜欢用这首歌作为讲解虚拟语气的范例。If I Were A Boy的意思是假如我是个男孩，相对于歌者身为女性来说，这显然是一种假设，且与现在的事实相反，因此我们需要使用虚拟语气。虚拟语气的定义是：虚拟语气用来表示说话人的主观愿望或假想，而不表示客观存在的事实，所说的是一个条件，不一定是事实，或与事实相反。虚拟语气通过谓语动词的特殊形式来表示。在表达与现有事实相反的假设时，虚拟语气中所有的be动词都要使用were，而不用was。</p><p style="line-height: 3em;">与此同时，特别注意的是，引导词if并不能作为虚拟语气的标志。你必须明白你所使用的if究竟是引导的一般条件状语从句表达「如果」的意思，还是作为假设。只有作为假设时（通常与事实相反），才需要使用虚拟语气。如果仅仅作为一般条件状语从句，那么只需遵循从句用一般现在时，主句用一般将来时的原则就可以。</p><p style="line-height: 3em;">2. 第三行 I’d roll out of bed in the morning，此处I&#39;d是I would的缩写，其实也是遵循了从句一般时，主句将来时的基本原则，仅仅因为此处是虚拟语气，所以要用助动词would而不用will。</p><p style="line-height: 3em;">3. 此外，还有几个短语值得我们学习:</p><p style="line-height: 3em;">roll out of bed：其实就是起床的意思，但比 get up要形象生动得多。结合roll「滚」的本意，大家可以仔细体会一下。</p><p style="line-height: 3em;">throw on what I wanted：穿上我想穿的衣服，这个短语需要结合语境理解。</p><p style="line-height: 3em;">chase after girls：泡妞把妹，不解释。</p><p style="line-height: 3em;">get confronted for it：这里的意思是我打了某人，且不用负责。get confronted for sth 是个值得记忆的短语。confront本意是指面对，但在使用时，我们一般用它的被动式，用作be confronted with sth。</p><p style="line-height: 3em;">在欧美流行歌曲中，往往包含了大量的时态及语态的应用范例，非常值得我们学习。再比如One Republic的代表作Counting Stars，开头第一句为Lately, I&#39;ve been, I&#39;ve been losing sleep，Dreaming about the things that we could be，这里首先涉及到了现在完成进行时的使用，这个时态是我们平时使用时经常会主观回避的一个时态，因为中文没有与之相对应的语法形式，算是一个比较冷门的语法点。通过背诵这句歌词，其实我们可以很好地掌握这种时态的用法。当我们需要描述某件事从某一时间开始，一直持续到现在，或者刚刚终止，或者可能仍然要继续下去，便用have+been+doing来进行表述。同时，这一句歌词最后的we could be也值得推究。这里为什么要使用could？这句歌词的意思是：最近我一直在失眠，梦想着那些我们可以做的事。其实也是虚拟语气的一种表达。</p><p style="line-height: 3em;">欧美流行歌曲我想大家不用我推荐了吧，满大街都是。不过在这一类里，我还是想重点推荐下One Republic，Beyoncé，Jason Mraz以及Michael Jackson的歌曲。当然最对我们学习者来时，即便是Justin Bieber的歌，也有很多值得学习的地方，大家一定不要歧视！</p><p style="line-height: 3em;">下一讲，准备讲讲摇滚乐的歌词吧，着实是个大工程。如果你想听什么摇滚乐队的歌词语法分析，请联系我！</p><p style="line-height: 3em;">用正确的姿势听英文歌&nbsp; &lt;三&gt;</p><p style="line-height: 3em;">Intro:</p><p style="line-height: 3em;">上次「用正确的姿势听英文歌——欧美流行篇」发表之后，居然有两位同学不约而同地想看Hotel California的分析，我想以此来打开摇滚篇章还挺合适。所以酝酿了一整个星期，终于，一篇在手机上阅读会比较吃力的文章面世了。I&#39;m sorry. 你们应该回我：It doesn&#39;t matter.不过真的很长，做好心理准备，对这首歌没兴趣的，你按右上分享给别人就是，我不会嫌弃你的。还有在看的英文老师们，希望对你们的教学有帮助哦亲！</p><p style="line-height: 3em;">如果你喜欢哪首英文歌，也欢迎给我们留言，我也会给你写一篇分析。</p><p style="line-height: 3em;">这个系列其实并不是教你如何从头开始学习英语，而只不过是探讨下英文歌中的英语知识。至于想从头好好学习英语的同学们，敬请期待我之后在此推送的简易教程。学不会你来我家打我。</p><p style="line-height: 3em;">正文：</p><p style="line-height: 3em;">Hotel California应该是大家相当耳熟能详的一首歌了，更是被部分听众戏称为「加州招待所」。关于这首歌本身，已经存在大量的分析和研究，大家随便网上一搜索便可知。所以我在这里不想再去重复那些老掉牙的故事，而是从新的视角，从英文语言的角度来给大家解读老鹰乐队的这首传世之作。</p><p style="line-height: 3em;">大家可以带上耳机，欣赏这首旷世经典迷人的双吉他编配以及超长前奏，然后开始听我给您说点儿语言上的故事。</p><p style="line-height: 3em;">首先我还是想和大家聊会这首歌的背景及涵义，这有助于之后各位更好地理解歌词分析。大家普遍认为这首歌与毒品、欲望、堕落之类的字眼有关，而至于加州旅店是否真有其地，也一直众说纷纭。创作者Don Henley 曾解释说：「我们是一群来自中西部州中产阶层背景的年轻人，加州旅馆是我们对洛杉矶的上流社会的理解。它可看做是对总是追求奢淫生活的美国的一个象征，而不仅仅是关于加州和毕利华山区。」我在准备这篇文章的时候，在网站songmeaning上倒是有了意外发现，一位听者说道，自己在普林斯顿大学就读期间，导师曾指出Hotel California 其实就是指的洛杉矶L.A，而在加州旅店中发生的种种，其实隐射的就是当时老鹰乐队进入洛杉矶制作专辑看到的城市整体状态以及唱片业的种种不堪。我觉得这是个非常有意思的观点。将老鹰乐队于1977年发行的专辑Hotel California作为整体来看，我们似乎可以寻找到这种观点的蛛丝马迹。</p><p style="line-height: 3em;">这张专辑一共包含如下九首歌：</p><p style="line-height: 3em;">&quot;Hotel California&quot;&nbsp; 加州旅店</p><p style="line-height: 3em;">&quot;New Kid in Town&quot;&nbsp; 城市新丁</p><p style="line-height: 3em;">&quot;Life in the Fast Lane&quot; 快车道上的人生</p><p style="line-height: 3em;">&quot;Wasted Time&quot;&nbsp; &nbsp; 浪费的光阴</p><p style="line-height: 3em;">&quot;Wasted Time (Reprise)&quot;</p><p style="line-height: 3em;">&quot;Victim of Love&quot;&nbsp; 爱的受害者</p><p style="line-height: 3em;">&quot;Pretty Maids All in a Row&quot;&nbsp; 美丽的女仆们</p><p style="line-height: 3em;">&quot;Try and Love Again&quot;&nbsp; 试着再爱一次</p><p style="line-height: 3em;">&quot;The Last Resort&quot;&nbsp; 最后的乐土</p><p style="line-height: 3em;">从这几首歌的歌名来看，我们依稀可以感觉到乐队成员作为大城市新丁，对眼前的一切感到新鲜，迷茫，虽然无所适从，但又身不由己。这些情感都可以从他们的歌词中摸索出来，同时，此后的分析也将涉及到一个重要的概念：隐喻（Metaphor）。</p><p style="line-height: 3em;">在我的理解中，Hotel California整首歌的灵魂在于We haven&#39;t had that spirit here since nineteen sixty nine 这句歌词，其中对于spirit和1969隐喻的理解相当关键。Spirit表面上指的是烈酒（雪碧是sprite），但spirit还有另一层意思，意为精神，与之后的1969联系起来（1969年为第一届伍德斯托克音乐节），可以明白这里暗指自由精神，以及嬉皮年代。所以我认为这首歌可以看做是对具有真正自由精神的嬉皮年代告别，同时带着嬉皮年代的恶性进入到物欲的现代社会。没有好坏与对错，我们只是注定在这里，扮演着自己的角色。你会死去，但你永远不能离开。</p><p style="line-height: 3em;">然后我们来看歌词。下面我将选取部分比较有意义的歌词来分析（整首歌实在太长了，我有耐心写，你们也没耐心看。）</p><p style="line-height: 3em;">第一段：</p><p style="line-height: 3em;">On a dark desert highway, cool wind in my hair</p><p style="line-height: 3em;">Warm smell of colitas rising up through the air</p><p style="line-height: 3em;">Up ahead in the distance, I saw a shimmering light</p><p style="line-height: 3em;">My head grew heavy and my sight grew dimmer</p><p style="line-height: 3em;">I had to stop for the night</p><p style="line-height: 3em;">1. desert是一个具有名词、形容的、动词三种词性的多功能单词，这里应该取其形容词性，意为荒废的。动词意为抛弃，名词则是沙漠。同时，这个单词再多加一个s，变成dessert，便是我们最爱的甜品。Dessert without S for Sugar is like desert.</p><p style="line-height: 3em;">2. Colitas来自于墨西哥使用的西语口语，原意为烤烟，这里暗指大麻。大麻可以看做是嬉皮年代的物质代表。</p><p style="line-height: 3em;">3.shimmering是动词shimmer的-ing形式做形容词，这里是形容微弱的灯光。</p><p style="line-height: 3em;">4. My head grew heavy and my sight grew dimmer我的头变得越来越重，我的视觉变得越来越模糊，描述的明显是吸食大麻后的身体反应，与之前的colitas呼应。</p><p style="line-height: 3em;">5. I had to stop for the night 我需要停留一个晚上。这里可以理解为暗喻在经历过嬉皮的60年代之后，我们也都陷入了迷茫，只是被“大麻”在无声息中指引着来到这里，而且身不由己地停留。</p><p style="line-height: 3em;">第二段：</p><p style="line-height: 3em;">There she stood in the doorway;</p><p style="line-height: 3em;">I heard the mission bell</p><p style="line-height: 3em;">And I was thinking to myself,</p><p style="line-height: 3em;">&quot;This could be Heaven or this could be Hell”</p><p style="line-height: 3em;">&quot;This could be Heaven or this could be Hell”这里既可以是天堂，也可以是地狱。这是对于进入70年代的美国的最好的描述。从嬉皮运动中走入现代社会，用「东北一家人」中巴颜格日顺的话来说就是，机遇与挑战并存。但是每个人都是在「飞」的状态，于是大家也不知道该怎么做，而更像是受到无声的指引，进入漫无目的的集体狂欢。</p><p style="line-height: 3em;">第三段：</p><p style="line-height: 3em;">Welcome to the Hotel California</p><p style="line-height: 3em;">Such a lovely place, such a lovely face</p><p style="line-height: 3em;">Plenty of room at the Hotel California</p><p style="line-height: 3em;">Any time of year, you can find it here</p><p style="line-height: 3em;">这里有个小细节值得注意。room这个单词，有两个意思。其一为房间，其二为空间。作为房间时候是可数名词，而空间是不可数。这里用的plenty of room而没有用rooms，所以这里的room应该是不可数名词，意思为了表达出加州旅店的包容性，或者说是，鱼龙混杂。</p><p style="line-height: 3em;">第四段：</p><p style="line-height: 3em;">Her mind is Tiffany-twisted, she got the Mercedes bends</p><p style="line-height: 3em;">She got a lot of pretty, pretty boys, that she calls friends</p><p style="line-height: 3em;">How they dance in the courtyard, sweet summer sweat.</p><p style="line-height: 3em;">Some dance to remember, some dance to forget</p><p style="line-height: 3em;">Her mind is Tiffany-twisted, she got the Mercedes bends 这也是一句比较精彩的歌词，Tiffany和Mercedes进行品牌上对应，而特意把Benz换成了bends，同音，但是意思上与twisted相对应，都为扭曲的意思，这里的写法类似于中文的对联。这一段主要表达出人在物欲面前的扭曲心态，以及醉生梦死。</p><p style="line-height: 3em;">第五段：</p><p style="line-height: 3em;">So I called up the Captain,</p><p style="line-height: 3em;">&quot;Please bring me my wine&quot;</p><p style="line-height: 3em;">He said, &quot;We haven&#39;t had that spirit here since nineteen sixty nine&quot;</p><p style="line-height: 3em;">这一段前文已经说过，意思属于自由的60年代已经过去了。</p><p style="line-height: 3em;">第六段：</p><p style="line-height: 3em;">Mirrors on the ceiling,</p><p style="line-height: 3em;">The pink champagne on ice</p><p style="line-height: 3em;">And she said &quot;We are all just prisoners here, of our own device&quot;</p><p style="line-height: 3em;">And in the master&#39;s chambers,</p><p style="line-height: 3em;">They gathered for the feast</p><p style="line-height: 3em;">They stab it with their steely knives,</p><p style="line-height: 3em;">But they just can&#39;t kill the beast</p><p style="line-height: 3em;">这一段可谓是对旅店内景象最详实的描述。We are all just prisoners here, of our own device 我们都自愿成为这里的囚徒，of our own device是自愿的意思，device除了设备之外，还有意愿之意。同时还描述了一群人在晚宴上彼此挥舞着小刀，却不敢与真正的野兽搏斗，这也反映出了当时社会的人心，爱窝里斗，却不敢面对真正的困难。</p><p style="line-height: 3em;">实话说，这里的场景描述，倒是让我想起了昆汀「杀出个黎明」里面那个沙漠中一夜之间僵尸爆棚的酒馆。</p><p style="line-height: 3em;">Last thing I remember, I was</p><p style="line-height: 3em;">Running for the door</p><p style="line-height: 3em;">I had to find the passage back</p><p style="line-height: 3em;">To the place I was before</p><p style="line-height: 3em;">&quot;Relax, &quot; said the night man,</p><p style="line-height: 3em;">&quot;We are programmed to receive.</p><p style="line-height: 3em;">You can check out any time you like,&nbsp; But you can never leave. &quot;</p><p style="line-height: 3em;">Running for the door, I had to find the passage back, To the place I was before 想要找到回去的路，试图逃离，但宿命又令人不可抗拒。We are programmed to receive.这是我特别欣赏的一句话，这里用program的被动式表达宿命感，联系到program的本意“程式”，会让人感觉到现代的社会的机械属性，每个人都像是一个程序，一个零件，丧失了真正的自由。</p><p style="line-height: 3em;">所以，我们认为嬉皮是真正的自由。但又也许根本不是。飞着飞着，我们就来到了这里，一个宿命安排的囚牢，假装自己是个快乐的囚徒。但根本不是。我们也许会缅怀过去的自由，但对眼前的沉沦又束手策。</p><p style="line-height: 3em;">但倘若再回一次六零年代，叶子依然要被点燃。因为我们对自由永远有着最原始自然的向往。</p><p><br/></p>', '2015-09-05', 85, 0, 2, 0, 0, 0, 1);
INSERT INTO `school_essay` (`id`, `mess_user`, `img`, `message_tittle`, `message_more`, `time`, `look`, `collect`, `good`, `bad`, `strong`, `only_me`, `evl`) VALUES
(4, '2207191788@qq.com', '55eaccd0f2697.gif', '经典英文歌短评', '<p></p><p><span style="position:absolute;z-index:1;left:0px;margin-top:662.0667px;width:798.0000px;height:86.0000px"><img width="798" height="86" src="/ueditor/themes/default/images/spacer.gif" word_img="file:///C:\\Users\\旭\\AppData\\Local\\Temp\\ksohtml\\wps89B.tmp.png" style="background:url(/ueditor/lang/zh-cn/images/localimage.png) no-repeat center center;border:1px solid #ddd"/></span><span style="position:absolute;z-index:-1;left:0px;margin-left:0.0000px;margin-top:0.0000px;width:796.0000px;height:844.0000px"><img width="796" height="844" src="/ueditor/themes/default/images/spacer.gif" word_img="file:///C:\\Users\\旭\\AppData\\Local\\Temp\\ksohtml\\wps919.tmp.png" style="background:url(/ueditor/lang/zh-cn/images/localimage.png) no-repeat center center;border:1px solid #ddd"/></span><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:华文行楷;color:rgb(255,0,0);font-size:32px"><br clear="all" style="page-break-before:always"/></span> </p><p><span style=";font-family:华文行楷;color:rgb(255,0,0);font-size:32px">　　　　　　　　 &nbsp; 短评</span></p><p>　　<span style="font-family: 华文新魏;font-weight: bold;font-size: 16px">因为我不太喜欢看电影,所以就把我从高中到现在积累的好的英文歌列出来，共６首。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">1.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">God&nbsp;Is&nbsp;a&nbsp;Girl</span><span style="font-family:宋体">》</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">《God&nbsp;is&nbsp;A&nbsp;Girl》由德国2001年新晋乐队Groove&nbsp;Coverage演唱的，收录于专辑《Covergirl》中。2003年，《God&nbsp;is&nbsp;A&nbsp;Girl》迅速登上德国DJ榜单的榜首，并问鼎2003年欧陆舞曲榜。G.C(Groove&nbsp;Coverage)凭借这首畅销单曲，开始在国际上走红，成名于流行乐坛。2006年，该歌曲创造中国海外音乐最高下载量的记录。《God&nbsp;is&nbsp;A&nbsp;Girl》被G.C粉丝称为该乐队的三大经典神曲之一。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">歌曲中，女主唱Verena有些慵懒而清脆柔美的声音，加上猛烈撞击碎拍呈现出的强烈的节奏感，典型的Dance节奏混搭POP及Trance舞曲的元素，使得歌曲极富节奏感和弹性，这样的演唱和演奏方式深深震撼和吸引了广大的音乐爱好者。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">2.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">On&nbsp;the&nbsp;radio</span><span style="font-family:宋体">》</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">Groove&nbsp;Coverage一首招牌的歌曲，出自专辑《21st&nbsp;Century》。Groove&nbsp;Coverage舞动精灵王族是德国2001年新晋的乐队，以其混合多种元素的舞曲风格迅速走红，驰名全欧。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">《On&nbsp;The&nbsp;Radio》在主题上继续舞动精灵在词作上的创意与大胆，充满幻想意境的电子舞曲，充分释放出不眠夜的寂寞与不安，在渴望能用音乐得到沟通与共鸣的传递中，让每一个听到《on&nbsp;the&nbsp;radio》的人都能感受到MELL女声中独有的舞曲式煽动性，直率且带给人极大悠远想象空间，突破以往舞曲带给人虽热情却尖锐无比的刚烈感受，用极富包容性的声腺撼动人心深处的敏感神经!成为舞动精灵舞曲风格充分成熟之后的又一饱满力作!作为精力制作的专辑《21世纪》的力推主打，可见其在专辑中无可替代的重金地位!</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">3.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">She</span><span style="font-family:宋体">》</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">出自Groove&nbsp;Coverage的&nbsp;《Years&nbsp;And&nbsp;50&nbsp;Days》专辑,一首比较老的歌,很是经典。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">“她”，是谁？——她性感迷人，她自由自在，她热爱自然，她享受生活，她是女孩存在的原因，她让你见过就无法忘记——她其实就是每一个挥洒青春时尚的动感女生。如果喜欢Groove&nbsp;Coverage的“Far&nbsp;Away&nbsp;From&nbsp;Home”，那么一定会对这首“She”爱不释手——依然整齐的鼓点伴奏、同样缓慢的旋律推进，只是，“She”少了前者的忧伤却多了几分轻松和浪漫，或者，还倾洒着几丝属于青春的狂野。如果说，略带沙哑颗粒状的声线是Groove&nbsp;Coverage组合的一大特色，那么“She”更是这种独特嗓音的代表。</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">&nbsp;</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">4.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">All&nbsp;for&nbsp;you</span><span style="font-family:宋体">》</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">爱司基地，被称为90年代欧洲歌坛最富有传奇色彩的流行组合。歌迷都戏称他们是瑞典的国宝级流行组合。这是一个兄妹组合，由Jenny、Jonas、Linn&nbsp;Berggren和他们和家庭好友Ulf&nbsp;Ekberg组成。在过去十年中，Ace&nbsp;Of&nbsp;Base一共卖出了3000万张专辑和1500万张单曲，他们的歌曲在世界各地的排行样上都曾达到首位，他们的音乐节奏独具特色，交融有雷鬼、迪斯科与欧洲电子音乐，加上轻松上口的歌词，营造出的欢愉气氛特别适合Party和迪斯科舞厅，以致于称霸全球流行音乐，让世人传唱。</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">作为年轻人，我还是比较喜欢这一类富有激情的英文歌，但是，我感觉这首歌给我的感觉是一种略带伤感的心情，所以不太常听，因为我比较乐观哈！但并不能否认它是一首好歌。</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">5.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">All&nbsp;I&nbsp;ever&nbsp;wanted</span><span style="font-family:宋体">》</span><span style="font-family:Times New Roman">[Basshunter</span><span style="font-family:宋体">版</span><span style="font-family:Times New Roman">]</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">瑞典歌手basshunter1984年出生于瑞典的哈尔姆斯塔德.basshunter&nbsp;因“boten&nbsp;anna”这首为电子竞技游戏而作的歌曲而出名，整个欧洲的音乐迷都在为这首歌曲而疯狂，这就使得这首歌在电子竞技比较发达的瑞典和荷兰都打进了前三名的排行榜。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">最先看到歌词时没什么感觉，看到翻译后才发现这首歌比较大胆开放，比较符合年轻人的口味，总体来说，超强的节奏感和动感的音乐让我百听不厌！</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style="font-family: 宋体;font-weight: bold;font-size: 19px">6.</span><span style="font-family: 宋体;font-weight: bold;font-size: 19px">《<span style="font-family:Times New Roman">Far&nbsp;away&nbsp;from&nbsp;home</span><span style="font-family:宋体">》</span></span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简介：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">Far&nbsp;Away&nbsp;From&nbsp;Home&nbsp;是Groove&nbsp;Coverage在2002发行的专辑《Covergirl》中的歌曲，歌名意译是：远离家乡。[注:该歌没有官方MV，网上的MV都是饭制版]</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">²&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">这首歌已被众多知名DJ誉为当今舞曲最为精华的传世之作，并且在中国网络的下载量达到600万多次。这首歌还被评为全球最好听500首歌之一。</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:隶书;color:rgb(255,0,0);font-size:19px">简评：</span></p><p style="margin-left:28px;line-height:150%"><span style="font-family:Wingdings;font-weight:bold;font-size:14px">v&nbsp;</span><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">就歌词而言，很显然，歌曲作者所说的“家”不是指家乡。实际上，“家”指的是心灵的归宿，这首Far&nbsp;Away&nbsp;From&nbsp;Home描写了人们一种无依无靠的精神状态，含蓄地表现出都市人欢笑背后内心的无奈、精神的空虚与迷茫。尽管歌曲中隐藏的情感相当消极，但歌手并没有绝望，因为有爱的存在。爱是唯一的精神依靠，它带来了希望，尽管有时也带来痛苦。但一切努力都是值得的，因为爱“let&nbsp;us&nbsp;shine”（让我们发光）。</span></p><p style=";text-align:justify;text-justify:inter-ideograph;line-height:150%"><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">&nbsp;</span></p><p style=";text-align:justify;text-justify:inter-ideograph;line-height:150%"><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">&nbsp;</span></p><p style=";text-align:justify;text-justify:inter-ideograph;line-height:150%"><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">&nbsp;</span></p><p style=";text-align:justify;text-justify:inter-ideograph;line-height:150%"><span style=";font-family:幼圆;font-weight:bold;font-size:14px">－－－－－－－－－－－－－－－－－－－－－－－－－－２０１５／９/５　星期六</span></p><p style=";text-align:justify;text-justify:inter-ideograph;line-height:150%"><span style=";font-family:华文新魏;font-weight:bold;font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><br/></p>', '2015-09-05', 713, 0, 5, 0, 0, 0, 7),
(5, '1576701411@qq.com', '56058d6baf915.jpg', '石河子大学校园生活网', '<h2 style="margin: 35px 0px 15px; padding: 0px 0px 5px; font-size: 16px; line-height: 24px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(222, 223, 225); clear: both; font-family: Arial, SimSun; white-space: normal;">概述</h2><p style="margin-top: 0px; padding: 0px; text-indent: 2em; word-wrap: break-word; line-height: 24px; font-family: Arial, SimSun; font-size: 14px; white-space: normal;">石河子大学校园生活网是由石河子大学信息科学与技术学院软件开发部打造的一款免费的线上校园生活服务平台，包括校园二手信息,失物招领,寻物启事,其他信息,校园活动,校园话题,文章随笔,视频分享,学习分享等，让同学们享受最便捷的校园生活服务。</p><h2 style="margin: 35px 0px 15px; padding: 0px 0px 5px; font-size: 16px; line-height: 24px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(222, 223, 225); clear: both; font-family: Arial, SimSun; white-space: normal;">产品与服务</h2><p style="margin-top: 0px; padding: 0px; text-indent: 2em; word-wrap: break-word; line-height: 24px; font-family: Arial, SimSun; font-size: 14px; white-space: normal;">校园生活网为石河子大学的同学提供一个线上校园生活服务平台，最大程度的让同学们享受到了便利，减少了同学们对二手资源尤其是书本的浪费，对丢失物品的困扰，以及分享自己的学习经验，促进了同学们的学习交流。</p><h2 style="margin: 35px 0px 15px; padding: 0px 0px 5px; font-size: 16px; line-height: 24px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(222, 223, 225); clear: both; font-family: Arial, SimSun; white-space: normal;">发展历程</h2><p style="margin-top: 0px; padding: 0px; text-indent: 2em; word-wrap: break-word; line-height: 24px; font-family: Arial, SimSun; font-size: 14px; white-space: normal;">2015年8月，经过一个暑假的努力，石河子大学第一个线上校园生活服务平台诞生了。</p><h2 style="margin: 35px 0px 15px; padding: 0px 0px 5px; font-size: 16px; line-height: 24px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(222, 223, 225); clear: both; font-family: Arial, SimSun; white-space: normal;">团队管理</h2><p style="margin-top: 0px; padding: 0px; text-indent: 2em; word-wrap: break-word; line-height: 24px; font-family: Arial, SimSun; font-size: 14px; white-space: normal;">校园生活网是由石河子大学信息科学与技术学院软件开发部打造，软件开发部成立于2008年，是由石河子大学一群IT爱好者组成，主要成员为大一，大二，大三学生。是一个集网站建设，平面设计，硬件维护，软件开发为一体的在校学生创业团队。部门长期做校内外多个项目，有良好的软件开发经验，和学习氛围。我们非常欢迎有兴趣的同学前来加入我们，一起进步；一起探讨自己的经验。</p><p><br/></p>', '2015-09-26', 128, 0, 2, 0, 0, 0, 0);

