--
-- 表的结构 `school_message`
--

CREATE TABLE IF NOT EXISTS `school_message` (
  `id` int(100) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `username` varchar(32) NOT NULL COMMENT '留言用户',
  `message_id` varchar(32) NOT NULL COMMENT '消息id',
  `message` varchar(500) NOT NULL COMMENT '留言内容',
  `time` date NOT NULL COMMENT '留言数间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='聊天记录表' AUTO_INCREMENT=1 ;

--
-- 转存表中的数据 `school_message`
--


-- --------------------------------------------------------

--
-- 表的结构 `school_message_2`
--

CREATE TABLE IF NOT EXISTS `school_message_2` (
  `id` int(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `message_username` varchar(32) NOT NULL COMMENT '发布者id',
  `image` varchar(100) NOT NULL DEFAULT '-' COMMENT '上传图片',
  `img2` varchar(32) NOT NULL DEFAULT '-' COMMENT '图2',
  `img3` varchar(32) NOT NULL DEFAULT '-' COMMENT '图3',
  `img4` varchar(32) NOT NULL DEFAULT '-' COMMENT '图4',
  `message_tittle` varchar(30) NOT NULL COMMENT '消息标题',
  `message_more` text NOT NULL COMMENT '详情描述',
  `span_1` varchar(32) NOT NULL COMMENT '主要标签',
  `span_2` varchar(32) NOT NULL COMMENT '标签2',
  `span_3` varchar(32) NOT NULL COMMENT '标签3',
  `span_4` varchar(32) NOT NULL COMMENT '标签4',
