--
-- 表的结构 `school_part`
--

CREATE TABLE IF NOT EXISTS `school_part` (
  `id` int(100) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `username` varchar(32) NOT NULL,
  `part_name` varchar(32) NOT NULL COMMENT '活动名称',
  `part_place` varchar(32) NOT NULL COMMENT '活动地点',
  `part_star_date` date NOT NULL COMMENT '活动开始日期',
  `part_star_time` time NOT NULL COMMENT '活动开始时间2',
  `part_over_date` date NOT NULL COMMENT '活动结束日期',
  `part_over_time` time NOT NULL COMMENT '活动结束时间',
  `part_image` varchar(32) NOT NULL COMMENT '活动海报',
  `part_theme` varchar(32) NOT NULL COMMENT '活动主题',
  `part_style` varchar(32) NOT NULL COMMENT '活动形式',
  `span_1` varchar(10) NOT NULL COMMENT '自定义标签１',
  `span_2` varchar(10) NOT NULL COMMENT '自定义标签２',
  `span_3` varchar(10) NOT NULL COMMENT '自定义标签３',
  `part_message` text NOT NULL COMMENT '活动详细内容',
  `good` int(10) NOT NULL DEFAULT '0' COMMENT '赞',
  `look` int(10) NOT NULL DEFAULT '0' COMMENT '浏览',
  `sign_up` int(10) NOT NULL DEFAULT '0' COMMENT '报名',
  `collect` int(10) NOT NULL DEFAULT '0' COMMENT '收藏',
  `is_strong` tinyint(1) NOT NULL DEFAULT '0' COMMENT '强力推荐',
  `shar` int(100) NOT NULL DEFAULT '0' COMMENT '活动分享次数',
  `pass` tinyint(1) NOT NULL DEFAULT '0' COMMENT '审核通过',
  `tel` varchar(32) NOT NULL COMMENT '发布者电话',
  `evl` varchar(32) NOT NULL DEFAULT '0' COMMENT '评论次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='校园活动信息发布' AUTO_INCREMENT=6 ;

--
-- 转存表中的数据 `school_part`
--

INSERT INTO `school_part` (`id`, `username`, `part_name`, `part_place`, `part_star_date`, `part_star_time`, `part_over_date`, `part_over_time`, `part_image`, `part_theme`, `part_style`, `span_1`, `span_2`, `span_3`, `part_message`, `good`, `look`, `sign_up`, `collect`, `is_strong`, `shar`, `pass`, `tel`, `evl`) VALUES
(4, '1576701411@qq.com', '手工橡皮章制作—｛闺蜜的约会｝手工课程', '（广东深圳）皇庭广场 F-cup cafe', '2015-09-01', '00:00:00', '2015-09-02', '00:00:00', '55eb03bc19a39.jpg', '手工', 'DIY', '橡皮章', '手工', '橡皮章', '<p style="line-height: 3em;">轻巧简单的手工作品</p><p style="line-height: 3em;">只需使用小型的雕刻刀具在橡皮砖上进行雕刻</p><p style="line-height: 3em;">即可制作出能反复盖印的图案</p><p style="line-height: 3em;">相对于石头和木头材质的章底</p><p style="line-height: 3em;"><img src="/ueditor/php/upload/image/20150905/1441465259566477.jpg" title="T2pbh7XuVaXXXXXXXX_!!479751912.jpg" alt="T2pbh7XuVaXXXXXXXX_!!479751912.jpg" style="border: 0px;vertical-align: middle;height: auto"/>&nbsp;</p><p style="line-height: 3em;">刻好的橡皮章结合不同颜色的印泥可以制造出各种效果</p><p style="line-height: 3em;">橡皮章制作最早流行于日本、韩国，随后传至台湾，近来才开始在内地受到欢迎</p><p style="line-height: 3em;">橡皮章的橡皮材质在印章制作过程中会更容易驾驭些</p><p style="line-height: 3em;">不需要很深厚的雕刻技巧</p><p style="line-height: 3em;">跟着指示就能完成心中想要图案</p><p style="line-height: 3em;">&nbsp;</p><p><br/></p><p style="line-height: 3em;"><img src="/ueditor/php/upload/image/20150905/1441465260866731.jpg" title="T1MljlXmJpXXXXdGc5_054610.jpg_400x400.jpg" alt="T1MljlXmJpXXXXdGc5_054610.jpg_400x400.jpg" style="border: 0px;vertical-align: middle;height: auto"/>&nbsp;<img src="/ueditor/php/upload/image/20150905/1441465261767112.jpg" title="T2shyXXxNXXXXXXXXX_!!479751912.jpg" alt="T2shyXXxNXXXXXXXXX_!!479751912.jpg" style="border: 0px;vertical-align: middle;height: auto"/>&nbsp;<img src="/ueditor/php/upload/image/20150905/1441465262968622.jpg" title="T2xQ4WXsRaXXXXXXXX_!!479751912.jpg" alt="T2xQ4WXsRaXXXXXXXX_!!479751912.jpg" width="300" height="400" style="border: 0px;vertical-align: middle;height: 400px;width: 300px"/><br/></p><p><br/></p><p style="line-height: 3em;">课程&nbsp;时间：&nbsp; 8月29日 星期日</p><p style="line-height: 3em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3：00pm－5：00 pm</p><p style="line-height: 3em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="line-height: 3em;">课程 价格：</p><p style="line-height: 3em;">&nbsp;手工章&nbsp;1:00 pm—3:30pm &nbsp; 230／人&nbsp;</p><p style="line-height: 3em;">&nbsp;<br/></p><p style="line-height: 3em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;课程 安排：</p><p style="line-height: 3em;">1.制作所需材料及工具的准备及介绍。</p><p style="line-height: 3em;">2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老师演示教学（约15分钟）。</p><p style="line-height: 3em;">3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;学员制作，老师以及助理辅导。</p><p style="line-height: 3em;">4.下午茶以及拍摄时间</p><p><br/></p><p><br/></p><p style="line-height: 3em;">创意的起点，优雅的生活以及正能量的传播，关注活动行＋闺蜜的约会平台</p><p><br/></p><p style="line-height: 3em;"><img src="/ueditor/php/upload/image/20150905/1441465266112453.jpg" title="386684577776613135.jpg" alt="386684577776613135.jpg" width="455" height="614" style="border: 0px;vertical-align: middle;height: 614px;width: 455px"/><br/></p><p style="line-height: 3em;">&nbsp;<br/></p><p><br/></p><p style="line-height: 3em;">咖啡，花艺，创意手工，烘培，下午茶聚会－带你离开繁琐的生活以及工作。</p><p style="line-height: 3em;">约会主旨：优雅的起点，创意的生活，以及正能量传播。快来加入闺蜜的约会，周末来一个优雅的小旅程。</p><p><br/></p>', 1, 773, 0, 0, 0, 0, 1, '18290777865', '0'),
(5, '1576701411@qq.com', '校园生活网', '校园生活网', '2015-10-01', '00:00:00', '2020-10-01', '00:00:00', '560760f610630.jpg', '欢迎小伙伴们免费使用', '免费使用', '线上平台', '信息学院', '原创作品', '<p style="line-height: 2em;">经过信息学院小伙伴们一个假期的辛苦，终于为同学们搭建了石河子大学第一个校园生活类线上服务平台，不是买的也不是模版替换的！绝对的原创作品哦！<img src="http://img.baidu.com/hi/ldw/w_0014.gif"/>，同学们多多支持，由于网站的维护是需要一定费用的！但我们坚持完全免费，也不靠小广告赚钱！我们做这个网站的初衷就是尽可能的解决同学们在校园生活中遇到的难题，也许有一天你丢东西了真的很急，而且就算别人捡到了也不知该如何联系你，发QQ群也会很快被覆盖，但会有这么一个线上平台能够尽可能的帮助你，包括一些其他功能，像二手交易、学习分享都是很实用的！我们搭建这个平台纯粹为同学们带来便利，我们不像社会上的那些商家，想方设法的从同学们的腰包里赚钱！请同学们多多支持！如果有一天这个平台真的帮助了你，你可以选择捐赠我们，多少你们随意！</p><p style="line-height: 2em;">我们的支付宝账号：18399357349</p><p style="line-height: 2em;">昵称：Oceanliao</p>', 5, 786, 0, 0, 0, 0, 1, '18290777865', '9');

