INSERT INTO `school_study` (`id`, `mess_user`, `img`, `message_tittle`, `message_more`, `time`, `look`, `collect`, `good`, `bad`, `strong`, `only_me`, `evl`) VALUES
(1, '1576701411@qq.com', '55eaf4b11ab19.jpg', '日期时间显示程序', '<pre class="brush:cpp;toolbar:false">/*\r\nC++题目11：日期时间显示程序\r\n（1）定义一个时间结构体类型time，能提供时、分、秒组成的时间，并提供时间增加的方法（按秒）。\r\n（2）定义一个日期结构体类型Date，记录年、月、日，并提供日期增加的方法（按天）。\r\n（3）定义个日期时间结构体类型DateTime，除具有年、月、日、时、分、秒外，还增加国名和与格林威治时间的差。\r\n编一应用程序，输入年、月、日、时、分、秒，在屏幕上模拟显示一电子计时器，不断输出下一秒的时间和日期，以及格林威治的日期和时间。\r\n注意，当对日期增加1天后，会涉及所谓的&quot;进位&quot;问题：首先算出本&quot;日&quot;所在的月份具有的天数N，若加1之后的&quot;日&quot;数值超过所在的月份具有的天数N时，&quot;进位&quot;到月，而月份若超过12时还要&quot;进位&quot;到年。\r\n思考：可对该类增加你所考虑到的其他功能，如，对日期进行输入，计算二日期的间隔天数，计算某日期为星期几，打印对象当前数据的y年m月的月历，一次增加若干天，对两个日期进行其他比较运算等。\r\n*/\r\n#include&lt;iostream&gt;\r\n#include&nbsp;&lt;windows.h&gt;\r\n#include&lt;cstdio&gt;\r\n#include&nbsp;&lt;time.h&gt;&nbsp;\r\nusing&nbsp;namespace&nbsp;std;\r\nbool&nbsp;IsLeap(int&nbsp;year)&nbsp;&nbsp;//判断是否是闰年\r\n{\r\nreturn&nbsp;(year%4&nbsp;==&nbsp;0)&amp;&amp;&nbsp;(year%100&nbsp;!=&nbsp;0)&nbsp;||&nbsp;(year%400&nbsp;==0);\r\n}\r\nint&nbsp;Month_Day(int&nbsp;year,int&nbsp;month)&nbsp;//返回一个月的天数\r\n{\r\nint&nbsp;day&nbsp;=&nbsp;0;&nbsp;\r\nif(month==1||month==3||month==5||month==7||month==8||month==10||month==12)\r\nday&nbsp;=&nbsp;31;&nbsp;&nbsp;\r\nelse&nbsp;if(month==4||month==6||month==9||month==11)\r\nday&nbsp;=&nbsp;30;\r\nelse&nbsp;if(month==2)\r\nday&nbsp;=&nbsp;28&nbsp;+&nbsp;IsLeap(year);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;day+1;&nbsp;&nbsp;\r\n}\r\nclass&nbsp;Time&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//定义时间类\r\n{\r\npublic:\r\nint&nbsp;hour;&nbsp;//时\r\nint&nbsp;minute;&nbsp;//分\r\nint&nbsp;second;&nbsp;//秒\r\nTime(int&nbsp;h,int&nbsp;m,int&nbsp;s);\r\n~Time(){};\r\nvoid&nbsp;add();\r\nvoid&nbsp;showTime();\r\n};\r\nTime::Time(int&nbsp;h,int&nbsp;m,int&nbsp;s):hour(h),minute(m),second(s)//采用成员初始化列表对成员赋值\r\n{}\r\nvoid&nbsp;Time::add()&nbsp;//时间增加的方法\r\n{\r\nif(second&lt;60)\r\n++second;\r\nif(second==60)\r\n{\r\n++minute;\r\nsecond=0;\r\n}\r\nif(minute==60)\r\n{\r\n++hour;\r\nminute=0;\r\n}\r\nif(hour==24)\r\n{\r\nhour=0;\r\n}&nbsp;\r\n}\r\nvoid&nbsp;Time::showTime()&nbsp;//输出时间的方法\r\n{\r\ncout&lt;&lt;&quot;\\t\\t※&quot;&lt;&lt;&quot;\\t\\t&quot;&lt;&lt;hour&lt;&lt;&quot;时&quot;&lt;&lt;&quot;:&quot;&lt;&lt;minute&lt;&lt;&quot;分&quot;&lt;&lt;&quot;:&quot;&lt;&lt;second&lt;&lt;&quot;秒&quot;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;&quot;&lt;&lt;endl;\r\n//cout&lt;&lt;&quot;\\t\\t&quot;&lt;&lt;hour&lt;&lt;&quot;时&quot;&lt;&lt;&quot;:&quot;&lt;&lt;minute&lt;&lt;&quot;分&quot;&lt;&lt;&quot;:&quot;&lt;&lt;second&lt;&lt;&quot;秒&quot;&lt;&lt;endl;\r\n}\r\nclass&nbsp;date&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//定义日期类\r\n{\r\npublic:\r\nint&nbsp;year;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//年\r\nint&nbsp;month;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//月\r\nint&nbsp;day;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//日\r\ndate(int&nbsp;y,int&nbsp;m,int&nbsp;d);\r\n~date(){};\r\nvoid&nbsp;add();&nbsp;&nbsp;//日期增加的方法\r\nvoid&nbsp;showdate();\r\n};\r\ndate::date(int&nbsp;y,int&nbsp;m,int&nbsp;d):year(y),month(m),day(d)//采用成员初始化列表对成员赋值\r\n{}\r\nvoid&nbsp;date::add()//日期增加的方法\r\n{\r\nif(day&lt;Month_Day(year,month))\r\nday++;\r\nif(day==Month_Day(year,month))\r\n{\r\nday&nbsp;=&nbsp;1;\r\nmonth++;\r\n}\r\nif(month&gt;12)\r\n{\r\nmonth&nbsp;=&nbsp;1;\r\nyear++;\r\n}\r\n}\r\nvoid&nbsp;date::showdate()&nbsp;//输出时间的方法\r\n{\r\ncout&lt;&lt;endl&lt;&lt;endl;\r\ncout&lt;&lt;&quot;\\t\\t※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;&quot;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&quot;&lt;&lt;&quot;\\t\\t&quot;&lt;&lt;year&lt;&lt;&quot;年&quot;&lt;&lt;&quot;:&quot;&lt;&lt;month&lt;&lt;&quot;月&quot;&lt;&lt;&quot;:&quot;&lt;&lt;day&lt;&lt;&quot;日&quot;&lt;&lt;endl;\r\n}\r\nclass&nbsp;DateTime:public&nbsp;date,Time&nbsp;&nbsp;//日期时间类\r\n{\r\nstring&nbsp;country;&nbsp;//国家名\r\nint&nbsp;time_differ;&nbsp;//时间差\r\npublic:\r\nDateTime(int&nbsp;h,int&nbsp;mi,int&nbsp;s,int&nbsp;y,int&nbsp;m,int&nbsp;d);\r\n~DateTime(){};\r\nvoid&nbsp;add();&nbsp;//日期增加的方法\r\nvoid&nbsp;showDateTime();\r\n};\r\nDateTime::DateTime(int&nbsp;y,int&nbsp;m,int&nbsp;d,int&nbsp;h,int&nbsp;mi,int&nbsp;s):date(y,m,d),Time(h,mi,s)\r\n{}\r\nvoid&nbsp;DateTime::add()\r\n{\r\nif(second&lt;0)\r\n{\r\nsecond=60+second;\r\n--minute;\r\n}\r\nif(second&lt;60&nbsp;&amp;&amp;&nbsp;second&gt;=0)\r\n++second;\r\nif(second==60)\r\n{\r\n++minute;\r\nsecond=0;\r\n}\r\nif(minute&lt;0)\r\n{\r\nminute=60+minute;\r\n--hour;\r\n}\r\nif(minute==60)\r\n{\r\n++hour;\r\nminute=0;\r\n}\r\nif(hour&lt;0)\r\n{\r\nhour=24+hour;\r\n--day;\r\n}\r\nif(hour==24)\r\n{\r\nhour=0;\r\n++day;\r\n}&nbsp;\r\nif(day&lt;=0)\r\n{\r\nday=30+day;\r\n--month;\r\n}\r\nif(day==Month_Day(year,month))\r\n{\r\nday&nbsp;=&nbsp;1;\r\n++month;\r\n}\r\nif(month&lt;=0)\r\n{\r\nmonth=12+month;\r\n--year;\r\n}\r\nif(month&gt;12)\r\n{\r\nmonth&nbsp;=&nbsp;1;\r\n++year;\r\n}\r\n}\r\nvoid&nbsp;DateTime::showDateTime()\r\n{\r\ndate::showdate();\r\nTime::showTime();\r\n}\r\n//DayInYear能根据给定的日期，求出它在该年的第几天\r\nint&nbsp;DayInYear(int&nbsp;year,&nbsp;int&nbsp;month,&nbsp;int&nbsp;day)\r\n{\r\n&nbsp;&nbsp;&nbsp;&nbsp;//int&nbsp;_day&nbsp;=&nbsp;0;\r\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;DAY[12]={31,28,31,30,31,30,31,31,30,31,30,31};\r\n&nbsp;&nbsp;&nbsp;&nbsp;if(IsLeap(year))\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;DAY[1]&nbsp;=&nbsp;29;\r\n&nbsp;&nbsp;&nbsp;&nbsp;for(int&nbsp;i=0;&nbsp;i&lt;month&nbsp;-&nbsp;1;&nbsp;++i)\r\n&nbsp;&nbsp;&nbsp;&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;day&nbsp;+=&nbsp;DAY[i];\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;day;\r\n}\r\n//求日期间隔\r\nint&nbsp;yearDay(int&nbsp;year,&nbsp;int&nbsp;month,&nbsp;int&nbsp;day,int&nbsp;year1,&nbsp;int&nbsp;month1,&nbsp;int&nbsp;day1)\r\n{\r\nint&nbsp;sum=0;\r\nif(year==year1)\r\n{\r\nint&nbsp;d=DayInYear(year,month,day)-DayInYear(year1,month1,day1);\r\nif(d&lt;0)\r\nd=-d;\r\nreturn&nbsp;d;\r\n}\r\nelse&nbsp;if(year&gt;year1)\r\n{\r\nfor(int&nbsp;i=year1+1;i&lt;year;i++)//若两日期间隔大于一年,则累加他们之间整年的天数\r\n{\r\nif(IsLeap(i))\r\nsum=sum+366;\r\nelse\r\nsum=sum+365;\r\n}\r\nif(IsLeap(year))\r\nreturn&nbsp;DayInYear(year,month,day)+(366-DayInYear(year1,month1,day1))+sum;\r\nelse\r\nreturn&nbsp;DayInYear(year,month,day)+(365-DayInYear(year1,month1,day1))+sum;\r\n}\r\nelse&nbsp;if(year&lt;year1)\r\n{\r\nfor(int&nbsp;i=year+1;i&lt;year1;i++)//若两日期间隔大于一年,则累加他们之间整年的天数\r\n{\r\nif(IsLeap(i))\r\nsum=sum+366;\r\nelse\r\nsum=sum+365;\r\n}\r\nif(IsLeap(year))\r\nreturn&nbsp;DayInYear(year1,month1,day1)+(366-DayInYear(year,month,day))+sum;\r\nelse\r\nreturn&nbsp;DayInYear(year1,month1,day1)+(365-DayInYear(year,month,day))+sum;\r\n}\r\n}\r\nvoid&nbsp;localtime()//当前系统时间\r\n{\r\ntime_t&nbsp;t;&nbsp;\r\ntm&nbsp;*lt;&nbsp;\r\nt&nbsp;=&nbsp;time(NULL);&nbsp;//历史时间清空\r\nlt&nbsp;=&nbsp;localtime(&amp;t);&nbsp;\r\ncout&lt;&lt;lt-&gt;tm_year+1900&lt;&lt;&quot;年&quot;&lt;&lt;lt-&gt;tm_mon&lt;&lt;&quot;月&quot;&lt;&lt;lt-&gt;tm_yday-150&lt;&lt;&quot;日&quot;&lt;&lt;&quot;星期&quot;&lt;&lt;lt-&gt;tm_wday&lt;&lt;&quot;&nbsp;&nbsp;&quot;&lt;&lt;lt-&gt;tm_hour&lt;&lt;&quot;:&quot;&lt;&lt;lt-&gt;tm_min&lt;&lt;&quot;:&quot;&lt;&lt;lt-&gt;tm_sec&lt;&lt;endl;&nbsp;\r\n}\r\nvoid&nbsp;birthday(int&nbsp;y,int&nbsp;m,int&nbsp;d)//从出生到现在的年,月,日&nbsp;&nbsp;&nbsp;\r\n{\r\ntime_t&nbsp;t;&nbsp;\r\ntm&nbsp;*lt;&nbsp;\r\nt&nbsp;=&nbsp;time(NULL);&nbsp;//历史时间清空\r\nlt&nbsp;=&nbsp;localtime(&amp;t);&nbsp;\r\nint&nbsp;day=yearDay(y,m,d,lt-&gt;tm_year+1900,lt-&gt;tm_mon,lt-&gt;tm_yday-150);\r\ncout&lt;&lt;&quot;从您出生到现在共经历了&quot;&lt;&lt;(lt-&gt;tm_year+1900)-y&lt;&lt;&quot;年&nbsp;&nbsp;&quot;&lt;&lt;&quot;共经历了&quot;&lt;&lt;((lt-&gt;tm_year+1900)-y)*12&lt;&lt;&quot;月&nbsp;&nbsp;&quot;&lt;&lt;&quot;共经历了&quot;&lt;&lt;day&lt;&lt;&quot;天&quot;&lt;&lt;endl;\r\n}\r\nvoid&nbsp;calendar(int&nbsp;y,int&nbsp;m)\r\n{\r\nint&nbsp;cal[6][7];\r\nint&nbsp;c=1;\r\nint&nbsp;firstday=(1&nbsp;+&nbsp;2*m+3*(m+1)/5+y+y/4-y/100+y/400)%7+1;//蔡勒(Zeller)公式&nbsp;计算y年m月的第一天是星期几\r\nint&nbsp;Day=Month_Day(y,m)-1;&nbsp;//计算一个月的天数\r\ncout&lt;&lt;&quot;&nbsp;一&nbsp;二&nbsp;三&nbsp;四&nbsp;五&nbsp;六&nbsp;日&nbsp;&quot;&lt;&lt;endl;\r\nfor(int&nbsp;i=0;i&lt;6;i++)\r\n{\r\nfor(int&nbsp;n=0;n&lt;7;n++)\r\n{\r\nif(n&nbsp;&lt;&nbsp;firstday&nbsp;&amp;&amp;&nbsp;i==0)\r\ncal[i][n]=0;\r\nelse\r\n{\r\ncal[i][n]=c;\r\nc++;\r\n}\r\n}\r\n}\r\nc=0;\r\nfor(&nbsp;i=0;i&lt;6;i++)\r\n{\r\nfor(int&nbsp;n=0;n&lt;7;n++)\r\n{\r\nif(cal[i][n]==0)\r\n{\r\ncout&lt;&lt;&quot;&nbsp;&nbsp;&nbsp;&quot;;\r\n}\r\nif(c!=Day&amp;&amp;cal[i][n]!=0)\r\n{\r\nprintf(&quot;%3d&quot;,cal[i][n]);\r\nc++;\r\n}\r\n}\r\ncout&lt;&lt;endl;\r\n}\r\n}\r\nbool&nbsp;pass_date(int&nbsp;y,int&nbsp;m,&nbsp;int&nbsp;d)//日期验证函数\r\n{\r\nif(d&gt;Month_Day(y,m)||m&lt;0)\r\nreturn&nbsp;0;\r\nelse&nbsp;if(m&lt;0||m&gt;12)\r\nreturn&nbsp;0;\r\nelse&nbsp;if(y&lt;1000||y&gt;9999)\r\nreturn&nbsp;0;\r\nelse\r\nreturn&nbsp;1;\r\n}\r\nbool&nbsp;pass_time(int&nbsp;h,int&nbsp;m,&nbsp;int&nbsp;s)//日期验证函数\r\n{\r\nif(h&gt;23||h&lt;0)\r\nreturn&nbsp;0;\r\nelse&nbsp;if(m&lt;0||m&gt;59)\r\nreturn&nbsp;0;\r\nelse&nbsp;if(s&lt;0||s&gt;59)\r\nreturn&nbsp;0;\r\nelse\r\nreturn&nbsp;1;\r\n}\r\nint&nbsp;main()\r\n{\r\nwhile(1)\r\n{\r\nchar&nbsp;op;\r\ncout&lt;&lt;&quot;当前时间:&quot;;\r\nlocaltime();\r\ncout&lt;&lt;endl;\r\ncout&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&quot;&nbsp;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;&nbsp;&nbsp;&nbsp;a.显示电子计时器和格林威治的日期和时间&nbsp;※&quot;&nbsp;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;&nbsp;&nbsp;&nbsp;b.计算两日期间隔天数&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;※&quot;&nbsp;&lt;&lt;endl&nbsp;\r\n&lt;&lt;&quot;\\t\\t※&nbsp;&nbsp;&nbsp;&nbsp;c.计算某日期为星期几&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;※&quot;&nbsp;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;&nbsp;&nbsp;&nbsp;d.打印y年m月的月历&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;※&quot;&nbsp;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;&nbsp;&nbsp;&nbsp;e.从出生到现在的年,月,日&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;※&quot;&nbsp;&lt;&lt;endl\r\n&lt;&lt;&quot;\\t\\t※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;※&nbsp;&quot;&lt;&lt;endl&lt;&lt;endl;\r\ncout&lt;&lt;&quot;请输入操作&quot;&lt;&lt;endl;\r\ncin&gt;&gt;op;\r\nif(op==&#39;a&#39;)//显示电子计时器和格林威治的日期和时间\r\n{\r\nsystem(&quot;cls&quot;);\r\nint&nbsp;year=0,month=0,day=0,hour=0,minute=0,second=0;\r\ncout&lt;&lt;&quot;请输入初始日期:年&nbsp;月&nbsp;日&nbsp;如:2004&nbsp;2&nbsp;28&quot;&lt;&lt;endl;\r\ncin&gt;&gt;year&gt;&gt;month&gt;&gt;day;\r\ncout&lt;&lt;&quot;请输入初始时间:时&nbsp;分&nbsp;秒&nbsp;如:23&nbsp;59&nbsp;57&quot;&lt;&lt;endl;\r\ncin&gt;&gt;hour&gt;&gt;minute&gt;&gt;second;\r\nif(pass_time(hour,minute,second)&amp;&amp;pass_date(year,month,day))\r\n{\r\nDateTime&nbsp;clock(year,month,day,hour,minute,second);\r\nDateTime&nbsp;clock1(year,month,day,hour-8,minute,second);\r\nwhile(1)\r\n{\r\ncout&lt;&lt;&quot;你输入的时间&quot;&lt;&lt;endl;\r\nclock.add();\r\nclock.showDateTime();\r\ncout&lt;&lt;&quot;对应格林威治时间&quot;&lt;&lt;endl;\r\nclock1.add();\r\nclock1.showDateTime();\r\nSleep(1000);\r\nsystem(&quot;cls&quot;);\r\n}\r\n}\r\nelse&nbsp;\r\n{\r\ncout&lt;&lt;&quot;时间格式错误&quot;&lt;&lt;endl;\r\nsystem(&quot;pause&quot;);\r\n}\r\n}\r\nif(op==&#39;b&#39;)//计算两日期间隔天数\r\n{\r\nint&nbsp;year1,month1,day1,year,month,day;\r\ncout&lt;&lt;&quot;请输入第一个日期&nbsp;如:2014&nbsp;1&nbsp;1&quot;&lt;&lt;endl;\r\ncin&gt;&gt;year&gt;&gt;month&gt;&gt;day;\r\ncout&lt;&lt;&quot;请输入第二个日期&nbsp;如:2014&nbsp;1&nbsp;1&quot;&lt;&lt;endl;\r\ncin&gt;&gt;year1&gt;&gt;month1&gt;&gt;day1;\r\nif(pass_date(year,month,day)&amp;&amp;pass_date(year1,month1,day1))\r\n{\r\ncout&lt;&lt;&quot;两日期间隔天数&quot;&lt;&lt;endl;\r\ncout&lt;&lt;yearDay(year,month,day,year1,month1,day1)&lt;&lt;&quot;天&quot;&lt;&lt;endl;\r\n}\r\nelse&nbsp;cout&lt;&lt;&quot;时间格式错误&quot;&lt;&lt;endl;\r\nsystem(&quot;pause&quot;);\r\n}\r\nif(op==&#39;c&#39;)//计算某日期为星期几\r\n{\r\nint&nbsp;y,m,d;\r\ncout&lt;&lt;&quot;年&nbsp;月&nbsp;日&nbsp;如:&nbsp;2008&nbsp;12&nbsp;1&quot;&lt;&lt;endl;\r\ncin&gt;&gt;y&gt;&gt;m&gt;&gt;d;\r\nif(pass_date(y,m,d))\r\n{\r\ncout&lt;&lt;y&lt;&lt;&quot;年&quot;&lt;&lt;m&lt;&lt;&quot;月&quot;&lt;&lt;d&lt;&lt;&quot;日是星期&quot;;\r\ncout&lt;&lt;(d&nbsp;+&nbsp;2*m+3*(m+1)/5+y+y/4-y/100+y/400)%7+1&lt;&lt;endl;//蔡勒(Zeller)公式\r\n}\r\nelse&nbsp;cout&lt;&lt;&quot;时间格式错误&quot;&lt;&lt;endl;\r\nsystem(&quot;pause&quot;);\r\n}\r\nif(op==&#39;d&#39;)\r\n{\r\nint&nbsp;y,m;\r\ncout&lt;&lt;&quot;请输入年&nbsp;月&nbsp;如&nbsp;2015&nbsp;6&quot;&lt;&lt;endl;\r\ncin&gt;&gt;y&gt;&gt;m;\r\nif(pass_date(y,m,10))\r\ncalendar(y,m);\r\nelse&nbsp;cout&lt;&lt;&quot;时间格式错误&quot;&lt;&lt;endl;\r\nsystem(&quot;pause&quot;);\r\n}\r\nif(op==&#39;e&#39;)\r\n{\r\nint&nbsp;y,m,d;\r\ncout&lt;&lt;&quot;请输入您的生日&nbsp;如:1996&nbsp;3&nbsp;20&quot;&lt;&lt;endl;\r\ncin&gt;&gt;y&gt;&gt;m&gt;&gt;d;\r\nif(pass_date(y,m,d))\r\nbirthday(y,m,d);\r\nelse&nbsp;cout&lt;&lt;&quot;时间格式错误&quot;&lt;&lt;endl;\r\nsystem(&quot;pause&quot;);\r\n}\r\nsystem(&quot;cls&quot;);\r\n}\r\nreturn&nbsp;0;\r\n}</pre><p><br/></p>', '2015-09-05', 116, 0, 1, 0, 0, 0, 3),
(2, '1576701411@qq.com', '56248c355f471.png', 'Web 品质- 标准', '<h1>Web 品质-&nbsp;标准</h1><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><p>根据web标准编写您的页面有助于改善您的网站品质。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2>HTML 标准</h2><p>XHTML 是最新的 HTML 标准，是用 XML 重新表达的 HTML 4.01。</p><p>根据 HTML 4.01 编写页面可使您的站点尽可能地接近 XHTML 标准。</p><p>阅读更多有关&nbsp;<a href="http://www.runoob.com/html/">HTML</a>&nbsp;的内容。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2>CSS 标准</h2><p>对于高品质的站点来说，使用层叠样式表（CSS）是将内容与样式分离的首选方式。通过使用 CSS，您能够在一个单独的文档中存储有关页面样式的所有信息。</p><p>所有现代的 web 浏览器均支持 CSS 1 和 CSS 2 标准。</p><p>对于不同的浏览器，使用 CSS 都可以改进网站的品质，并提高可读性。同时还可以极大地减少您的网站开发成本。</p><p>阅读更多关于&nbsp;<a href="http://www.runoob.com/css/">CSS</a>&nbsp;的内容。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2>Web 验证</h2><p>web 验证工具是一种软件程序，可根据 web 标准对您的网站进行检查。</p><p>当您使用验证工具检查过HTML, XHTML 或 CSS 文档之后，验证器就会根据您选择的标准返回一系列所发现的错误。通常，验证器会返回所发现错误的行号。</p><p>请确保你在发布页面前进行验证成为一种习惯。</p><p>阅读更多关于&nbsp;<a href="http://www.runoob.com/web/web-validate.html">页面验证</a>&nbsp;的内容。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2>WAI - 无障碍网页倡议</h2><p>WAI 指的是无障碍网页倡议( &quot;Web Accessibility Initiative&quot;)，是由 W3C 发起的。</p><p>WAI 协调全球的组织通过六项主要的工作领域来提升因特网的可用性：技术、指导方针、工具、教育、研究以及开发。</p><p>您能够通过根据 WAI 的指导方针编写的页面，来改善网站的品质，并使得您的站点可用于更多人群（及浏览器）。</p><p>您将在本教程稍后的章节学习更多有关 WAI 的内容。</p><p><br/></p>', '2015-10-19', 107, 0, 2, 0, 0, 0, 1),
(3, '1576701411@qq.com', '56248c9394be3.png', 'Web 品质 - 样式表', '<h1 style="border: 0px; margin: 0px 0px 10px; padding: 0px; font-size: 2.1em; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">Web 品质 -&nbsp;<span class="color_h1" style="border: 0px; margin: 0px; padding: 0px; color: #64854C;">样式表</span></h1><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><p class="intro" style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">使用样式表对于提升网页品质至关重要。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">不要使用 &lt;font&gt; 标签！</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">应使用 CSS 来设置显示网页上的字体尺寸。请不要使用 font 标签。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">使用 &lt;font&gt; 标签会增加文档的规模，而且使您每次改变标准文字尺寸的工作成为一场梦魇。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">请设想一下下面的情况：</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">一天您决定修改网站中所有标题的颜色和尺寸。通过 CSS，您只需要修改一行就可以做到这一点。假如您使用了 &lt;font&gt;标签，那么您需要把网站中所有页面的所有标题都修改一遍。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20151019/1445235827332205.gif" alt="lamp.gif" style="border: 0px; margin: 0px; padding: 0px; height: auto;"/>使用样式替代 &lt;font&gt; 标签可使我们更轻松地为网页制作高质量的界面。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">请勿使用固定的字体尺寸</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">不要使用固定的尺寸值。请始终使用相对的尺寸值。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">这项建议最重要的理由是无法通过浏览器重新调整固定尺寸的大小。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">您的访问者会使用不同的设备（显示器）、不同的浏览环境（光线）以及可能的残疾（弱视）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">例如，可以把某人的文字尺寸设置为100%（或者 medium ），主标题设置为140%（或者 x-large ），而次级的标题设置为120%（或者 large ），这样用户就可以使用浏览器来重新设定最喜欢的尺寸了。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);"><strong>注意：</strong>&nbsp;通过调整网页的文本尺寸的功能，也可以改变打印页面的文字数目。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">请勿使用很小的默认字体尺寸</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">一些网站会使用很小的文字尺寸，这样就可以向每张页面&quot;塞&quot;入更多的内容，或者使页面看上去更&quot;时髦&quot;。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">再次重申，使用不同的设备（显示器）、不同的浏览环境（光线）以及可能的残疾（弱视），都可能对用户造成阅读障碍。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20151019/1445235827332205.gif" alt="lamp.gif" style="border: 0px; margin: 0px; padding: 0px; height: auto;"/>请不要逼迫用户每次访问你的站点时都要放大文本的尺寸。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">始终使用一致的背景颜色</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">大部分网页都会为不同的文本元素使用颜色。标题和链接的颜色通常与正文的文本颜色是不同的。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">作为一位 web 设计者，您应当意识到的事实是，您的访问者能够修改默认的颜色选项。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">如果您为 web 元素定义了颜色，那么同样应当定义背景颜色。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">如果不定义背景颜色，那么您的网站可能会被糟糕的颜色组合搞砸（比如红色背景上面的亮红文字，或者深色文本搭配的深色背景）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);"><img src="/ueditor/php/upload/image/20151019/1445235827332205.gif" alt="lamp.gif" style="border: 0px; margin: 0px; padding: 0px; height: auto;"/>&nbsp;如果您不规定背景颜色，可能会使文本很难被识别。</p><p><br/></p>', '2015-10-19', 130, 0, 1, 0, 0, 0, 0),
(4, '1576701411@qq.com', '56248ce52255c.jpg', 'Web 品质- 可读性', '<h1 style="border: 0px; margin: 0px 0px 10px; padding: 0px; font-size: 2.1em; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">Web 品质-&nbsp;<span class="color_h1" style="border: 0px; margin: 0px; padding: 0px; color: #64854C;">可读性</span></h1><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><p class="intro" style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">正确使用字体和颜色可使您的网站更易阅读。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">留意颜色的对比</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">对于视力不太好的人或者对于不太好的显示设备来说，黑底白字或者白底黑字是最佳的。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">在亮色背景上的灰色文字，对比度是很差的：</p><table width="730" style="width: 698px;"><tbody style="border: 0px; margin: 0px; padding: 0px;"><tr style="border: 0px; margin: 0px; padding: 0px;" class="firstRow"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(238, 238, 238);">Grey text on a white background (#EEEEEE)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(204, 204, 204);">Grey text on a white background (#CCCCCC)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(170, 170, 170);">Grey text on a white background (#AAAAAA)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(136, 136, 136);">Grey text on a white background (#888888)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(102, 102, 102);">Grey text on a white background (#666666)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(68, 68, 68);">Grey text on a white background (#444444)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(34, 34, 34);">Grey text on a white background (#222222)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(17, 17, 17);">Grey text on a white background (#111111)</td></tr></tbody></table><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">在暗色背景上的灰色文字，其对比度同样很差：</p><table width="730" style="width: 698px;"><tbody style="border: 0px; margin: 0px; padding: 0px;"><tr style="border: 0px; margin: 0px; padding: 0px;" class="firstRow"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(34, 34, 34);">Grey text on a black background (#222222)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(68, 68, 68);">Grey text on a black background (#444444)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(102, 102, 102);">Grey text on a black background (#666666)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(136, 136, 136);">Grey text on a black background (#888888)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(170, 170, 170);">Grey text on a black background (#AAAAAA)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(204, 204, 204);">Grey text on a black background (#CCCCCC)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(221, 221, 221);">Grey text on a black background (#DDDDDD)</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(238, 238, 238);">Grey text on a black background (#EEEEEE)</td></tr></tbody></table><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">某些搭配 - 比如黑色和红色，黑色和蓝色，黄色和绿色 - 都会使人产生视觉疲劳：</p><table width="730" style="width: 698px;"><tbody style="border: 0px; margin: 0px; padding: 0px;"><tr style="border: 0px; margin: 0px; padding: 0px;" class="firstRow"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(0, 0, 0); background-color: rgb(255, 0, 0);">Black text on a red background</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em;"><br/></td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(0, 0, 0); background-color: rgb(0, 0, 255);">Black text on a blue background</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em;"><br/></td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(255, 255, 0); background-color: rgb(0, 255, 0);">Yellow text on a green background</td></tr></tbody></table><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">这些搭配并不糟糕：</p><table width="730" style="width: 698px;"><tbody style="border: 0px; margin: 0px; padding: 0px;"><tr style="border: 0px; margin: 0px; padding: 0px;" class="firstRow"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(0, 0, 0); background-color: rgb(238, 238, 238);">Black text on a grey background</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em;"><br/></td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(0, 0, 0); background-color: rgb(240, 248, 255);">Black text on a light blue</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em;"><br/></td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(0, 0, 0); background-color: rgb(250, 235, 215);">Black text on antique white</td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em;"><br/></td></tr><tr style="border: 0px; margin: 0px; padding: 0px;"><td style="border: 0px; margin: 0px; padding: 0px; font-size: 1em; color: rgb(255, 255, 255); background-color: rgb(25, 25, 112);">White text on dark blue</td></tr></tbody></table><p><br style="color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"/></p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">留意字母的间距</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">对于视力比较弱的读者，比较近的字母间距会带有不小的阅读困难。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; letter-spacing: 0.1mm; background-color: rgb(255, 255, 255);">文字间距适中的文本就比较容易阅读了。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; letter-spacing: -0.2mm; background-color: rgb(255, 255, 255);">文字间距少的文字难以阅读。</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">当心你的行距</h2><p><strong style="border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">以下行距易于阅读</strong><span style="color: #333333; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; background-color: #FFFFFF;"></span></p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 13.1999998092651px; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">具有良好的行距的文本<br/>更易阅读</p><p><strong style="border: 0px; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">以下行距难以阅读</strong><span style="color: #333333; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; background-color: #FFFFFF;"></span></p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 9.60000038146973px; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">行距小的文本&nbsp;<br/>难以阅读</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">避免花哨的字体</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">这字体容易阅读....</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 18px; font-family: &#39;cBlackadder ITC&#39;, &#39;Brush Script MT&#39;; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">这种字体是难以阅读....</p><hr style="color: rgb(212, 212, 212); height: 1px; border: 0px; clear: both; font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(212, 212, 212);"/><h2 style="border: 0px; margin: 2px 0px; padding: 0px; font-size: 1.8em; line-height: 1.8em; color: rgb(51, 51, 51); font-family: &#39;Open Sans&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, STHeiti, &#39;Microsoft Yahei&#39;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);">尽量少用斜体</h2><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">普通字体易于阅读。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; line-height: 2em; font-size: 12px; font-family: &#39;Microsoft Yahei&#39;, &#39;Helvetica Neue&#39;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);"><em>斜体字体不那么容易阅读。</em></p><p><br/></p>', '2015-10-19', 119, 0, 6, 0, 0, 0, 1);
INSERT INTO `school_study` (`id`, `mess_user`, `img`, `message_tittle`, `message_more`, `time`, `look`, `collect`, `good`, `bad`, `strong`, `only_me`, `evl`) VALUES
(5, '1576701411@qq.com', '567ef3494210a.png', 'ThinkPHP入门教程', '<p style="text-align:center"><span style=";font-family:宋体;font-weight:bold;font-size:27px">ThinkPHP<span style="font-family:宋体">入门教程</span></span></p><p><span style="font-family: 宋体;font-size: 16px">我们先了解下<span style="font-family:Calibri">PHP;</span></span></p><p><span style="font-family: 宋体;font-size: 16px">先安装<span style="font-family:Calibri">wamp&nbsp;</span><span style="font-family:宋体">下载网址&nbsp;</span></span><a href="http://www.btela.com/OceanFile/WampServer.rar"><span style="font-family: 宋体;color: #800080;font-size: 16px">http://www.btela.com/OceanFile/WampServer.rar</span></a></p><p><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p><span style="font-family: 宋体;font-size: 16px">解压后双击就可以安装了。</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">安装完成后会显示下面这个图标</span></p><p style="text-align:justify;text-justify:inter-ideograph"><img src="/ueditor/php/upload/image/20151227/1451160221220790.png" title="1451160221220790.png" alt="blob.png"/><span style=";font-family:Calibri;font-size:14px"></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">点击<span style="font-family:Calibri">Localhost&nbsp;</span><span style="font-family:宋体">会出现</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><br/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:Calibri;font-size:14px"></span><img src="/ueditor/php/upload/image/20151227/1451160252718122.png" title="1451160252718122.png" alt="blob.png"/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">如果显示的不是这个页面，单独来找我！</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">接下来点击<span style="font-family:Calibri">www</span><span style="font-family:宋体">目录</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><img src="/ueditor/php/upload/image/20151227/1451160262961052.png" title="1451160262961052.png" alt="blob.png"/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:Calibri;font-size:14px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">会有这两个文件</span></p><p style="text-align:justify;text-justify:inter-ideograph"><img src="/ueditor/php/upload/image/20151227/1451160315711481.png" title="1451160315711481.png" alt="blob.png"/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">新建</span><span style=";font-family:宋体;font-size:14px">&nbsp;test.php&nbsp;<span style="font-family:宋体">写入下面的代码：</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><img src="/ueditor/php/upload/image/20151227/1451160328221473.png" title="1451160328221473.png" alt="blob.png"/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:Calibri;font-size:14px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">保存到</span><span style=";font-family:宋体;font-size:14px">www<span style="font-family:宋体">目录，然后打开浏览器输入&nbsp;</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><a href="http://localhost/test.php"><span style="font-family: 宋体;color: #0000FF">http://localhost/test.php</span></a><span style=";font-family:宋体;font-size:14px">&nbsp;会出现</span></p><p style="text-align:justify;text-justify:inter-ideograph"><img src="/ueditor/php/upload/image/20151227/1451160341519491.png" title="1451160341519491.png" alt="blob.png"/></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">你们也发现了</span><span style=";font-family:宋体;font-size:14px">&nbsp;php&nbsp;<span style="font-family:宋体">和</span><span style="font-family:Calibri">c</span><span style="font-family:宋体">语言的语法很相似。</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">点击下面这个网址可以更方便的调试编辑，手机也可以访问。</span></p><p style="text-align:justify;text-justify:inter-ideograph"><a href="http://www.runoob.com/try/runcode.php?filename=demo_intro&type=php"><span style="font-family: 宋体;color: #0000FF">http://www.runoob.com/try/runcode.php?filename=demo_intro&amp;type=php</span></a><span style=";font-family:宋体;font-size:14px">&nbsp;菜鸟教程</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">我推荐你们用<span style="font-family:Calibri">sublime</span><span style="font-family:宋体">编写代码</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">下载地址&nbsp;：</span><a href="http://www.btela.com/OceanFile/SublimeText3.zip"><span style="font-family: 宋体;color: #0000FF">http://www.btela.com/OceanFile/SublimeText3.zip</span></a></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;color:#FF0000;font-size:14px">现在我说下<span style="font-family:Calibri">ThinkPHP</span><span style="font-family:宋体">框架，之前给你们的教学视频是</span><span style="font-family:Calibri">thinkPHP3.2</span><span style="font-family:宋体">版本的，之前我没注意你们都删了吧，我们统一学</span><span style="font-family:Calibri">3.1</span><span style="font-family:宋体">版本</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style=";font-family:宋体;font-size:14px">3.1<span style="font-family:宋体">框架下载地址&nbsp;</span></span><a href="http://www.btela.com/OceanFile/ThinkPHP.zip"><span style="font-family: 宋体;color: #800080;font-size: 16px">http://www.btela.com/OceanFile/ThinkPHP.zip</span></a></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">或者按照教程下载</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">3.1<span style="font-family:宋体">教程（</span></span><span style="font-family: 宋体;color: #FF0000;font-size: 16px">百度网盘在新疆需要在手机上访问</span><span style="font-family: 宋体;font-size: 16px">）</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">链接<span style="font-family:Calibri">:&nbsp;</span></span><a href="http://pan.baidu.com/s/1borRACv"><span style="font-family: 宋体;color: #0000FF;font-size: 16px">http://pan.baidu.com/s/1borRACv</span></a></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">密码<span style="font-family:Calibri">:&nbsp;y8v5</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">还有你们刚开始主要上这个网站学习</span></p><p style="text-align:justify;text-justify:inter-ideograph"><a href="http://www.5idev.com/p-thinkphp_tpl_display.shtml"><span style="font-family: 宋体;color: #0000FF;font-size: 16px">http://www.5idev.com/p-thinkphp_tpl_display.shtml</span></a></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">这个网站上手很快</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">最后看看<span style="font-family:Calibri">thinkPHP</span><span style="font-family:宋体">官网&nbsp;</span></span><a href="http://www.thinkphp.cn/"><span style="font-family: 宋体;color: #0000FF;font-size: 16px">http://www.thinkphp.cn/</span></a></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">假期</span><span style="font-family: 宋体;color: #FF0000;font-size: 16px">之前</span><span style="font-family: 宋体;font-size: 16px">的任务：</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">你们这段时间&nbsp;都写一些简单的功能，就用<span style="font-family:Calibri">ThinkPHP</span><span style="font-family:宋体">。不会的就问我或者百度。</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">&nbsp;</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">要实现的功能</span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">1.<span style="font-family:宋体">实现&nbsp;登陆&nbsp;注册&nbsp;修改个人信息</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">2.<span style="font-family:宋体">去阿里云官网注册个</span><span style="font-family:Calibri">15</span><span style="font-family:宋体">天免费的服务器，把写好的网站部署进去。</span></span></p><p style="text-align:justify;text-justify:inter-ideograph"><span style="font-family: 宋体;font-size: 16px">放假前每个人都要做好。百度搜都可以，只要你实现这个过程。</span></p><p><br/></p>', '2015-12-27', 1, 0, 1, 0, 0, 1, 0);

