--
-- 表的结构 `school_ueditor`
--

CREATE TABLE IF NOT EXISTS `school_ueditor` (
  `id` int(100) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(32) NOT NULL COMMENT '标题',
  `text` text NOT NULL COMMENT '内容',
  `time` date NOT NULL COMMENT '日期',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='百度Ueditor编辑器' AUTO_INCREMENT=1 ;

