CREATE TABLE IF NOT EXISTS `school_user` (
  `id` int(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `username` varchar(32) NOT NULL COMMENT '用户名',
  `usernumber` varchar(32) NOT NULL COMMENT '用户账号',
  `password` varchar(32) NOT NULL COMMENT '密码',
  `lasttime` date NOT NULL COMMENT '最后一次登陆日期',
  `addtime` date NOT NULL COMMENT '注册时间',
  `age` int(5) NOT NULL COMMENT '年龄',
  `start` varchar(5) NOT NULL COMMENT '星座',
  `user_level` int(32) NOT NULL DEFAULT '0' COMMENT '用户积分',
  `img` varchar(32) NOT NULL COMMENT '用户头像',
  `qq` varchar(20) NOT NULL COMMENT 'qq',
  `tel` varchar(20) NOT NULL COMMENT '电话',
  `s_year` varchar(20) NOT NULL COMMENT '年级',
  `avatar` varchar(32) NOT NULL COMMENT '头像',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='用户表' AUTO_INCREMENT=36 ;

